/**
 * Index command - list extracted sessions with statistics.
 * Agent sessions excluded - explore via Task tool calls in parent sessions.
 * Statistics are computed on-the-fly (not stored in metadata).
 */

import { readdir } from "node:fs/promises";
import { homedir } from "node:os";
import { join } from "node:path";
import { getHiveMindSessionsDir, readExtractedSession } from "../lib/extraction";
import { printError } from "../lib/output";
import type { ContentBlock, HiveMindMeta, KnownEntry } from "../lib/schemas";

interface SessionInfo {
  meta: HiveMindMeta;
  entries: Array<KnownEntry>;
}

const MONTH_NAMES = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

export function computeMinimalPrefixes(ids: Array<string>): Map<string, string> {
  const result = new Map<string, string>();
  const minLen = 4;

  for (const id of ids) {
    let len = minLen;
    while (len < id.length) {
      const prefix = id.slice(0, len);
      const conflicts = ids.filter((other) => other !== id && other.startsWith(prefix));
      if (conflicts.length === 0) break;
      len++;
    }
    result.set(id, id.slice(0, len));
  }

  return result;
}

function formatRelativeDateTime(
  rawMtime: string,
  prevDate: string,
  prevYear: string
): { display: string; date: string; year: string } {
  const dateObj = new Date(rawMtime);
  const year = String(dateObj.getFullYear());
  const month = MONTH_NAMES[dateObj.getMonth()];
  const day = String(dateObj.getDate()).padStart(2, "0");
  const hours = String(dateObj.getHours()).padStart(2, "0");
  const minutes = String(dateObj.getMinutes()).padStart(2, "0");

import { randomUUID } from "node:crypto";
import { mkdir, readFile, writeFile } from "node:fs/promises";
import { homedir } from "node:os";
import { join } from "node:path";

export const WORKOS_CLIENT_ID =
  process.env.HIVE_MIND_CLIENT_ID ?? "client_01KE10CYZ10VVZPJVRQBJESK1A";

export const AUTH_DIR = join(homedir(), ".claude", "hive-mind");
export const AUTH_FILE = join(AUTH_DIR, "auth.json");
/**
 * Get or create a persistent checkout ID for this project checkout.
 * Project-scoped and gitignored - each worktree gets its own ID.
 * @param hiveMindDir - The .claude/hive-mind directory for the project
 */
export async function getCheckoutId(hiveMindDir: string) {
  const checkoutIdFile = join(hiveMindDir, "checkout-id");
  try {
    const id = await readFile(checkoutIdFile, "utf-8");
    return id.trim();
  } catch {
    const id = randomUUID();
    await mkdir(hiveMindDir, { recursive: true });
    await writeFile(checkoutIdFile, id);
    // Ensure checkout-id is gitignored (worktree-specific)
    const gitignorePath = join(hiveMindDir, ".gitignore");
    try {
      const existing = await readFile(gitignorePath, "utf-8");
      if (!existing.includes("checkout-id")) {
        await writeFile(gitignorePath, `${existing.trimEnd()}\ncheckout-id\n`);

