     1/**
     2 * Index command - list extracted sessions with statistics.
     3 * Agent sessions excluded - explore via Task tool calls in parent sessions.
     4 * Statistics are computed on-the-fly (not stored in metadata).
     5 */
     6
     7import { readdir } from "node:fs/promises";
     8import { homedir } from "node:os";
     9import { join } from "node:path";
    10import { getHiveMindSessionsDir, readExtractedSession } from "../lib/extraction";
    11import { printError } from "../lib/output";
    12import type { ContentBlock, HiveMindMeta, KnownEntry } from "../lib/schemas";
    13
    14interface SessionInfo {
    15  meta: HiveMindMeta;
    16  entries: Array<KnownEntry>;
    17}
    18
    19const MONTH_NAMES = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
    20
    21export function computeMinimalPrefixes(ids: Array<string>): Map<string, string> {
    22  const result = new Map<string, string>();
    23  const minLen = 4;
    24
    25  for (const id of ids) {
    26    let len = minLen;
    27    while (len < id.length) {
    28      const prefix = id.slice(0, len);
    29      const conflicts = ids.filter((other) => other !== id && other.startsWith(prefix));
    30      if (conflicts.length === 0) break;
    31      len++;
    32    }
    33    result.set(id, id.slice(0, len));
    34  }
    35
    36  return result;
    37}
    38
    39function formatRelativeDateTime(
    40  rawMtime: string,
    41  prevDate: string,
    42  prevYear: string
    43): { display: string; date: string; year: string } {
    44  const dateObj = new Date(rawMtime);
    45  const year = String(dateObj.getFullYear());
    46  const month = MONTH_NAMES[dateObj.getMonth()];
    47  const day = String(dateObj.getDate()).padStart(2, "0");
    48  const hours = String(dateObj.getHours()).padStart(2, "0");
    49  const minutes = String(dateObj.getMinutes()).padStart(2, "0");
    50
    51  const date = `${month}${day}`;
    52  const time = `T${hours}:${minutes}`;
    53
    54  let display: string;
    55  if (year !== prevYear && prevYear !== "") {
    56    display = `${year}${date}${time}`;
    57  } else if (date !== prevDate) {
    58    display = `${date}${time}`;
    59  } else {
    60    display = time;
    61  }
    62
    63  return { display, date, year };
    64}
    65
    66interface SessionStats {
    67  userCount: number;
    68  linesAdded: number;
    69  linesRemoved: number;
    70  filesTouched: number;
    71  significantLocations: Array<string>;
    72  bashCount: number;
    73  fetchCount: number;
    74  searchCount: number;
    75}
    76
    77interface FileStats {
    78  added: number;
    79  removed: number;
    80}
    81
    82function printUsage(): void {
    83  console.log("Usage: index [--escape-file-refs]");
    84  console.log("\nList all extracted sessions with statistics, summary, and commits.");
    85  console.log("Agent sessions are excluded (explore via Task tool calls in parent sessions).");
    86  console.log("Statistics include work from subagent sessions.");
    87  console.log("\nOptions:");
    88  console.log("  --escape-file-refs  Escape @ symbols to prevent file reference interpretation");
    89  console.log("\nOutput columns:");
    90  console.log("  ID                   Session ID prefix (first 16 chars)");
    91  console.log("  DATETIME             Session modification time");
    92  console.log("  MSGS                 Total message count");
    93  console.log("  USER_MESSAGES        User message count");
    94  console.log("  BASH_CALLS           Bash commands executed");
    95  console.log("  WEB_FETCHES          Web fetches");
    96  console.log("  WEB_SEARCHES         Web searches");
    97  console.log("  LINES_ADDED          Lines added");
    98  console.log("  LINES_REMOVED        Lines removed");
    99  console.log("  FILES_TOUCHED        Number of unique files modified");
   100  console.log("  SIGNIFICANT_LOCATIONS Paths where >30% of work happened");
   101  console.log("  SUMMARY              Session summary or first user prompt");
   102  console.log("  COMMITS              Git commit hashes from the session");
   103}
