   105export async function index(): Promise<number> {
   106  const args = process.argv.slice(3);
   107
   108  if (args.includes("--help") || args.includes("-h")) {
   109    printUsage();
   110    return 0;
   111  }
   112
   113  const escapeFileRefs = args.includes("--escape-file-refs");
   114
   115  const cwd = process.cwd();
   116  const sessionsDir = getHiveMindSessionsDir(cwd);
   117
   118  let files: Array<string>;
   119  try {
   120    files = await readdir(sessionsDir);
   121  } catch {
   122    printError(`No sessions found. Run 'extract' first.`);
   123    return 1;
   124  }
   125
   126  const jsonlFiles = files.filter((f) => f.endsWith(".jsonl"));
   127  if (jsonlFiles.length === 0) {
   128    printError(`No sessions found in ${sessionsDir}`);
   129    return 1;
   130  }
   131
   132  // Load all sessions for subagent lookups
   133  const allSessions = new Map<string, SessionInfo>();
   134  for (const file of jsonlFiles) {
   135    const path = join(sessionsDir, file);
   136    const session = await readExtractedSession(path);
   137    if (session) {
   138      allSessions.set(session.meta.sessionId, session);
   139      // Also index by agentId for agent sessions
   140      if (session.meta.agentId) {
   141        allSessions.set(session.meta.agentId, session);
   142      }
   143    }
   144  }
   145
   146  // Filter to main sessions only
   147  const mainSessions = Array.from(allSessions.values()).filter((s) => !s.meta.agentId);
   148  mainSessions.sort((a, b) => b.meta.rawMtime.localeCompare(a.meta.rawMtime));
   149
   150  // Compute minimal unique prefixes for session IDs
   151  const sessionIds = mainSessions.map((s) => s.meta.sessionId);
   152  const idPrefixes = computeMinimalPrefixes(sessionIds);
   153
   154  console.log(
   155    "ID DATETIME MSGS USER_MESSAGES BASH_CALLS WEB_FETCHES WEB_SEARCHES LINES_ADDED LINES_REMOVED FILES_TOUCHED SIGNIFICANT_LOCATIONS SUMMARY COMMITS"
   156  );
   157  let prevDate = "";
   158  let prevYear = "";
   159  for (const session of mainSessions) {
   160    const prefix = idPrefixes.get(session.meta.sessionId) || session.meta.sessionId.slice(0, 8);
   161    const { line, date, year } = formatSessionLine(session, allSessions, cwd, prefix, prevDate, prevYear, escapeFileRefs);
   162    console.log(line);
   163    prevDate = date;
   164    prevYear = year;
   165  }
   166
   167  return 0;
   168}
   169
   170function formatSessionLine(
   171  session: SessionInfo,
   172  allSessions: Map<string, SessionInfo>,
   173  cwd: string,
   174  idPrefix: string,
   175  prevDate: string,
   176  prevYear: string,
   177  escapeFileRefs: boolean
   178): { line: string; date: string; year: string } {
   179  const { meta, entries } = session;
   180  const msgs = String(meta.messageCount);
   181  const rawSummary = findSummary(entries) || findFirstUserPrompt(entries) || "";
   182  const summary = escapeFileRefs ? rawSummary.replace(/@/g, "\\@") : rawSummary;
   183
   184  const commits = findGitCommits(entries).filter((c) => c.success);
   185  const commitList = commits
   186    .map((c) => c.hash || (c.message.length > 50 ? `${c.message.slice(0, 47)}...` : c.message))
   187    .join(" ");
   188
   189  // Compute stats including subagents
   190  const stats = computeSessionStats(entries, allSessions, new Set(), cwd);
   191
   192  // Format numbers, blank for 0
   193  const fmt = (n: number) => (n === 0 ? "" : String(n));
   194
   195  // Format datetime with relative display
   196  const { display: datetime, date, year } = formatRelativeDateTime(meta.rawMtime, prevDate, prevYear);
   197
   198  const line = [
   199    idPrefix,
   200    datetime,
   201    msgs,
   202    fmt(stats.userCount),
   203    fmt(stats.bashCount),
   204    fmt(stats.fetchCount),
   205    fmt(stats.searchCount),
   206    stats.linesAdded === 0 ? "" : `+${stats.linesAdded}`,
   207    stats.linesRemoved === 0 ? "" : `-${stats.linesRemoved}`,
   208    fmt(stats.filesTouched),
   209    stats.significantLocations.join(" "),
   210    summary,
   211    commitList,
   212  ].join(" ");
   213
   214  return { line, date, year };
   215}
   216
   217function computeSessionStats(
   218  entries: Array<KnownEntry>,
   219  allSessions: Map<string, SessionInfo>,
   220  visited: Set<string>,
   221  cwd: string
   222): SessionStats {
   223  const stats: SessionStats = {
   224    userCount: 0,
   225    linesAdded: 0,
   226    linesRemoved: 0,
   227    filesTouched: 0,
   228    significantLocations: [],
   229    bashCount: 0,
   230    fetchCount: 0,
   231    searchCount: 0,
   232  };
   233
   234  const fileStats = new Map<string, FileStats>();
   235  const subagentIds: Array<string> = [];
   236
   237  for (const entry of entries) {
   238    if (entry.type === "user") {
   239      const content = entry.message.content;
   240      if (typeof content === "string" || !Array.isArray(content)) {
   241        stats.userCount++;
   242      } else {
   243        const hasUserText = content.some((b) => b.type === "text");
   244        if (hasUserText) stats.userCount++;
   245      }
   246
   247      if ("agentId" in entry && typeof entry.agentId === "string") {
   248        subagentIds.push(entry.agentId);
   249      }
   250    }
   251
   252    if (entry.type === "assistant") {
   253      const content = entry.message.content;
   254      if (!Array.isArray(content)) continue;
   255
   256      for (const block of content) {
   257        if (block.type !== "tool_use" || !("name" in block)) continue;
   258
   259        const toolName = block.name;
   260        const input = block.input;
   261
   262        switch (toolName) {
   263          case "Edit": {
   264            const filePath = input.file_path;
   265            const oldString = input.old_string;
   266            const newString = input.new_string;
   267            if (typeof filePath === "string") {
   268              const current = fileStats.get(filePath) || { added: 0, removed: 0 };
   269              if (typeof oldString === "string") {
   270                current.removed += countLines(oldString);
   271              }
   272              if (typeof newString === "string") {
   273                current.added += countLines(newString);
   274              }
   275              fileStats.set(filePath, current);
   276            }
   277            break;
   278          }
   279          case "Write": {
   280            const filePath = input.file_path;
   281            const fileContent = input.content;
   282            if (typeof filePath === "string" && typeof fileContent === "string") {
   283              const current = fileStats.get(filePath) || { added: 0, removed: 0 };
   284              current.added += countLines(fileContent);
   285              fileStats.set(filePath, current);
   286            }
   287            break;
   288          }
   289          case "Bash":
   290            stats.bashCount++;
   291            break;
   292          case "WebFetch":
   293            stats.fetchCount++;
   294            break;
   295          case "WebSearch":
   296            stats.searchCount++;
   297            break;
   298          case "Task":
   299            break;
   300        }
   301      }
   302    }
   303  }
   304
   305  for (const agentId of subagentIds) {
   306    if (visited.has(agentId)) continue;
   307    visited.add(agentId);
   308
   309    const subSession = allSessions.get(agentId);
   310    if (!subSession) continue;
   311
   312    const subStats = computeSessionStats(subSession.entries, allSessions, visited, cwd);
   313    stats.linesAdded += subStats.linesAdded;
   314    stats.linesRemoved += subStats.linesRemoved;
   315    stats.bashCount += subStats.bashCount;
   316    stats.fetchCount += subStats.fetchCount;
   317    stats.searchCount += subStats.searchCount;
   318  }
   319
   320  for (const fs of fileStats.values()) {
   321    stats.linesAdded += fs.added;
   322    stats.linesRemoved += fs.removed;
   323  }
   324  stats.filesTouched = fileStats.size;
   325  stats.significantLocations = computeSignificantLocations(fileStats, cwd);
   326
   327  return stats;
   328}
   329
   330function countLines(s: string): number {
   331  if (!s) return 0;
   332  let count = 1;
   333  for (const c of s) {
   334    if (c === "\n") count++;
   335  }
   336  return count;
   337}
   338
   339interface PathNode {
   340  children: Map<string, PathNode>;
   341  added: number;
   342  removed: number;
   343}
   344
   345export function computeSignificantLocations(fileStats: Map<string, FileStats>, cwd: string): Array<string> {
   346  if (fileStats.size === 0) return [];
   347
   348  const root: PathNode = { children: new Map(), added: 0, removed: 0 };
   349  const cwdPrefix = cwd.replace(/^\//, "").replace(/\/$/, "") + "/";
   350  const homePrefix = homedir().replace(/^\//, "") + "/";
   351
   352  for (const [filePath, stats] of fileStats) {
   353    let normalizedPath = filePath.replace(/^\//, "");
   354    if (normalizedPath.startsWith(cwdPrefix)) {
   355      normalizedPath = normalizedPath.slice(cwdPrefix.length);
   356    } else if (normalizedPath.startsWith(homePrefix)) {
   357      normalizedPath = "~/" + normalizedPath.slice(homePrefix.length);
   358    }
   359    const parts = normalizedPath.split("/");
   360    let node = root;
   361
   362    for (const part of parts) {
   363      if (!node.children.has(part)) {
   364        node.children.set(part, { children: new Map(), added: 0, removed: 0 });
   365      }
   366      node = node.children.get(part)!;
   367    }
   368
   369    node.added = stats.added;
   370    node.removed = stats.removed;
   371  }
   372
   373  function calculateTotals(node: PathNode): { added: number; removed: number } {
   374    let added = node.added;
   375    let removed = node.removed;
   376    for (const child of node.children.values()) {
   377      const childTotals = calculateTotals(child);
   378      added += childTotals.added;
   379      removed += childTotals.removed;
   380    }
   381    node.added = added;
   382    node.removed = removed;
   383    return { added, removed };
   384  }
   385  calculateTotals(root);
   386
   387  const totalLines = root.added + root.removed;
   388  if (totalLines === 0) return [];
   389
   390  const SIGNIFICANT_THRESHOLD = 0.3;
   391  const DOMINANT_THRESHOLD = 0.5;
   392
   393  const results: Array<string> = [];
   394
   395  function findSignificant(node: PathNode, path: string) {
   396    const nodeLines = node.added + node.removed;
   397    const nodePercent = nodeLines / totalLines;
   398
   399    if (nodePercent <= SIGNIFICANT_THRESHOLD) return;
   400
   401    let dominantChild: { name: string; node: PathNode } | null = null;
   402    for (const [name, child] of node.children) {
   403      const childLines = child.added + child.removed;
   404      const childPercentOfParent = childLines / nodeLines;
   405      if (childPercentOfParent > DOMINANT_THRESHOLD) {
   406        dominantChild = { name, node: child };
   407        break;
   408      }
   409    }
   410
   411    if (dominantChild) {
   412      const childPath = path ? `${path}/${dominantChild.name}` : dominantChild.name;
   413      findSignificant(dominantChild.node, childPath);
   414    } else if (path) {
   415      const isDirectory = node.children.size > 0;
   416      results.push(isDirectory ? `${path}/` : path);
   417    }
   418  }
   419
   420  for (const [name, child] of root.children) {
   421    findSignificant(child, name);
   422  }
   423
   424  return results.slice(0, 3);
   425}
   426
   427const META_XML_TAGS = ["<command-name>", "<local-command-", "<system-reminder>"];
   428
   429function isMetaXml(text: string): boolean {
   430  const trimmed = text.trim();
   431  return META_XML_TAGS.some((tag) => trimmed.startsWith(tag));
   432}
   433
   434function isGarbageSummary(summary: string): boolean {
   435  const trimmed = summary.trim();
   436  return isMetaXml(trimmed) || trimmed.startsWith("Caveat:");
   437}
   438
   439function findSummary(entries: Array<KnownEntry>): string | undefined {
   440  const uuids = new Set<string>();
   441  const summaries: Array<{ summary: string; leafUuid?: string }> = [];
   442
   443  for (const entry of entries) {
   444    if ("uuid" in entry && typeof entry.uuid === "string") {
   445      uuids.add(entry.uuid);
   446    }
   447    if (entry.type === "summary") {
   448      summaries.push({ summary: entry.summary, leafUuid: entry.leafUuid });
   449    }
   450  }
   451
   452  for (const s of summaries) {
   453    if (s.leafUuid && uuids.has(s.leafUuid) && !isGarbageSummary(s.summary)) {
   454      return s.summary;
   455    }
   456  }
   457
   458  const lastSummary = summaries.at(-1)?.summary;
   459  return lastSummary && !isGarbageSummary(lastSummary) ? lastSummary : undefined;
   460}
   461
   462function findFirstUserPrompt(entries: Array<KnownEntry>): string | undefined {
   463  for (const entry of entries) {
   464    if (entry.type !== "user") continue;
   465    if ("isMeta" in entry && entry.isMeta === true) continue;
   466
   467    const content = entry.message.content;
   468    if (!content) continue;
   469
   470    let text: string | undefined;
   471    if (typeof content === "string") {
   472      text = content;
   473    } else if (Array.isArray(content)) {
   474      for (const block of content) {
   475        if (block.type === "text" && "text" in block && typeof block.text === "string") {
   476          text = block.text;
   477          break;
   478        }
   479      }
   480    }
   481
   482    if (text) {
   483      const trimmed = text.trim();
   484      if (isMetaXml(trimmed)) continue;
   485
   486      const firstLine = trimmed.split("\n")[0].trim();
   487      if (firstLine) {
   488        return firstLine.length > 100 ? `${firstLine.slice(0, 97)}...` : firstLine;
   489      }
   490    }
   491  }
   492  return undefined;
   493}
   494
   495interface GitCommit {
   496  hash: string | undefined;
   497  message: string;
   498  success: boolean;
   499}
   500
   501function findGitCommits(entries: Array<KnownEntry>): Array<GitCommit> {
   502  const commits: Array<GitCommit> = [];
   503  const pendingCommits = new Map<string, string>();
   504
   505  for (const entry of entries) {
   506    if (entry.type === "assistant") {
   507      const content = entry.message.content;
   508      if (!Array.isArray(content)) continue;
   509
   510      for (const block of content) {
   511        if (block.type === "tool_use" && "name" in block && block.name === "Bash") {
   512          const input = block.input;
   513          const command = input.command;
   514          if (typeof command === "string" && command.includes("git commit")) {
   515            const message = extractCommitMessage(command);
   516            if (message && "id" in block && typeof block.id === "string") {
   517              pendingCommits.set(block.id, message);
   518            }
   519          }
   520        }
   521      }
   522    } else if (entry.type === "user") {
   523      const content = entry.message.content;
   524      if (!Array.isArray(content)) continue;
   525
   526      for (const block of content) {
   527        if (block.type === "tool_result" && "tool_use_id" in block) {
   528          const toolUseId = block.tool_use_id;
   529          const message = pendingCommits.get(toolUseId);
   530          if (message) {
   531            const resultContent = getToolResultText(block.content as string | Array<ContentBlock> | undefined);
   532            const success = resultContent.includes("[") && !resultContent.includes("error");
   533            const hash = extractCommitHash(resultContent);
   534            commits.push({ hash, message, success });
   535            pendingCommits.delete(toolUseId);
   536          }
   537        }
   538      }
   539    }
   540  }
   541
   542  for (const message of pendingCommits.values()) {
   543    commits.push({ hash: undefined, message, success: true });
   544  }
   545
   546  return commits;
   547}
   548
   549function extractCommitHash(output: string): string | undefined {
   550  // Parse "[branch abc1234] message" format
   551  const match = output.match(/\[[\w/-]+\s+([a-f0-9]{7,})\]/);
   552  return match?.[1];
   553}
   554
   555function extractCommitMessage(command: string): string | undefined {
   556  // Heredoc: -m "$(cat <<'EOF'\nmessage\nEOF\n)"
   557  const heredocMatch = command.match(/<<['"]?EOF['"]?\s*\n([\s\S]*?)\n\s*EOF/);
   558  if (heredocMatch) {
   559    const firstLine = heredocMatch[1].trim().split("\n")[0].trim();
   560    if (firstLine) return firstLine;
   561  }
   562
   563  // -m "message" (not heredoc)
   564  const mFlagMatch = command.match(/git commit[^"']*-m\s*["'](?!\$\()([^"']+)["']/);
   565  if (mFlagMatch) return mFlagMatch[1].trim();
   566
   567  // Simple -m message (no quotes)
   568  const simpleMatch = command.match(/git commit[^-]*-m\s+(\S+)/);
   569  if (simpleMatch && !simpleMatch[1].startsWith('"') && !simpleMatch[1].startsWith("'")) {
   570    return simpleMatch[1];
   571  }
   572
   573  return undefined;
   574}
   575
   576function getToolResultText(content: string | Array<ContentBlock> | undefined): string {
   577  if (!content) return "";
   578  if (typeof content === "string") return content;
   579
   580  const parts: Array<string> = [];
   581  for (const block of content) {
   582    if (block.type === "text" && "text" in block) {
   583      parts.push(block.text);
   584    }
   585  }
   586  return parts.join("\n");
   587}
