    16export async function getCheckoutId(hiveMindDir: string) {
    17  const checkoutIdFile = join(hiveMindDir, "checkout-id");
    18  try {
    19    const id = await readFile(checkoutIdFile, "utf-8");
    20    return id.trim();
    21  } catch {
    22    const id = randomUUID();
    23    await mkdir(hiveMindDir, { recursive: true });
    24    await writeFile(checkoutIdFile, id);
    25    // Ensure checkout-id is gitignored (worktree-specific)
    26    const gitignorePath = join(hiveMindDir, ".gitignore");
    27    try {
    28      const existing = await readFile(gitignorePath, "utf-8");
    29      if (!existing.includes("checkout-id")) {
    30        await writeFile(gitignorePath, `${existing.trimEnd()}\ncheckout-id\n`);
    31      }
    32    } catch {
    33      await writeFile(gitignorePath, "checkout-id\n");
    34    }
    35    return id;
    36  }
    37}
