    10function DefaultErrorComponent({ error, reset }: { error: Error; reset: () => void }) {
    11  const isDev = process.env.NODE_ENV === 'development';
    12
    13  return (
    14    <div style={{ padding: '2rem', maxWidth: '600px', margin: '0 auto' }}>
    15      <h1 style={{ fontSize: '1.5rem', marginBottom: '1rem' }}>Something went wrong</h1>
    16      <p style={{ color: '#666', marginBottom: '1rem' }}>
    17        An unexpected error occurred. Please try again.
    18      </p>
    19      <button
    20        onClick={reset}
    21        style={{
    22          padding: '0.5rem 1rem',
    23          backgroundColor: '#3b82f6',
    24          color: 'white',
    25          border: 'none',
    26          borderRadius: '0.375rem',
    27          cursor: 'pointer',
    28        }}
    29      >
    30        Try again
    31      </button>
    32      {isDev && (
    33        <details style={{ marginTop: '1.5rem' }}>
    34          <summary style={{ cursor: 'pointer', color: '#666' }}>Error details</summary>
    35          <pre style={{ marginTop: '0.5rem', padding: '1rem', backgroundColor: '#f1f5f9', borderRadius: '0.375rem', overflow: 'auto', fontSize: '0.875rem' }}>
    36            {error.stack || error.message}
    37          </pre>
    38        </details>
    39      )}
    40    </div>
    41  );
    42}
    43
    44export function getRouter() {
    45  const CONVEX_URL = (import.meta as any).env.VITE_CONVEX_URL!;
    46  if (!CONVEX_URL) {
    47    throw new Error('missing VITE_CONVEX_URL env var');
    48  }
    49  const convex = new ConvexReactClient(CONVEX_URL);
    50  const convexQueryClient = new ConvexQueryClient(convex);
    51
    52  const queryClient = new QueryClient({
    53    defaultOptions: {
    54      queries: {
    55        queryKeyHashFn: convexQueryClient.hashFn(),
    56        queryFn: convexQueryClient.queryFn(),
    57        gcTime: 5000,
    58      },
    59    },
    60  });
    61  convexQueryClient.connect(queryClient);
    62
    63  const router = createRouter({
    64    routeTree,
    65    defaultPreload: 'intent',
    66    scrollRestoration: true,
    67    defaultPreloadStaleTime: 0,
    68    defaultErrorComponent: ({ error, reset }) => <DefaultErrorComponent error={error} reset={reset} />,
    69    defaultNotFoundComponent: () => <p>not found</p>,
    70    context: { queryClient, convexClient: convex, convexQueryClient },
    71    Wrap: ({ children }) => (
    72      <AuthKitProvider>
    73        <ConvexProviderWithAuth client={convexQueryClient.convexClient} useAuth={useAuthFromWorkOS}>
    74          {children}
    75        </ConvexProviderWithAuth>
    76      </AuthKitProvider>
    77    ),
    78  });
    79  setupRouterSsrQueryIntegration({ router, queryClient });
    80
    81  return router;
    82}
    83
    84function useAuthFromWorkOS() {
    85  const { loading, user } = useAuth();
    86  const { accessToken, getAccessToken } = useAccessToken();
    87
    88  const fetchAccessToken = useCallback(
    89    async ({ forceRefreshToken }: { forceRefreshToken: boolean }) => {
    90      if (!accessToken || forceRefreshToken) {
    91        return (await getAccessToken()) ?? null;
    92      }
    93
    94      return accessToken;
    95    },
    96    [accessToken, getAccessToken],
    97  );
    98
    99  return useMemo(
   100    () => ({
   101      isLoading: loading,
   102      isAuthenticated: !!user,
   103      fetchAccessToken,
   104    }),
   105    [loading, user, fetchAccessToken],
   106  );
   107}
