     1→import { createReadStream } from "node:fs";
     2→import { mkdir, readFile, readdir, stat, writeFile } from "node:fs/promises";
     3→import { createInterface } from "node:readline";
     4→import { homedir } from "node:os";
     5→import { basename, dirname, join } from "node:path";
     6→import { getCheckoutId } from "./config";
     7→import { getDetectSecretsStats, resetDetectSecretsStats, sanitizeDeep } from "./sanitize";
     8→import {
     9→  
    10→  HiveMindMetaSchema,
    11→  
    12→  parseKnownEntry
    13→} from "./schemas";
    14→import type {HiveMindMeta, KnownEntry} from "./schemas";
    15→
    16→const HIVE_MIND_VERSION = "0.1" as const;
    17→
    18→const INCLUDED_ENTRY_TYPES = ["user", "assistant", "summary", "system"] as const;
    19→
    20→export function* parseJsonl(content: string) {
    21→  for (const line of content.split("\n")) {
    22→    const trimmed = line.trim();
    23→    if (!trimmed) continue;
    24→    try {
    25→      yield JSON.parse(trimmed) as unknown;
    26→    } catch (error) {
    27→      if (process.env.DEBUG) {
    28→        console.warn("Skipping malformed JSONL line:", error);
    29→      }
    30→    }
    31→  }
    32→}
    33→
    34→function transformEntry(rawEntry: unknown) {
    35→  const entry = parseKnownEntry(rawEntry);
    36→  if (!entry) {
    37→    if (process.env.DEBUG) {
    38→      console.warn("Skipping unknown entry type");
    39→    }
    40→    return null;
    41→  }
    42→
    43→  if (INCLUDED_ENTRY_TYPES.includes(entry.type as (typeof INCLUDED_ENTRY_TYPES)[number])) {
    44→    return entry;
    45→  }
    46→
    47→  return null;
    48→}
    49→
    50→type ExtractedEntry = Exclude<ReturnType<typeof transformEntry>, null>;
    51→
    52→/** Returns the summary where leafUuid exists in the same session. */
    53→function findValidSummary(entries: Array<ExtractedEntry>) {
    54→  const uuids = new Set<string>();
    55→  const summaries: Array<{ summary: string; leafUuid?: string }> = [];
    56→
    57→  for (const entry of entries) {
    58→    if ("uuid" in entry && typeof entry.uuid === "string") {
    59→      uuids.add(entry.uuid);
    60→    }
    61→    if (entry.type === "summary") {
    62→      summaries.push({
    63→        summary: entry.summary,
    64→        leafUuid: entry.leafUuid,
    65→      });
    66→    }
    67→  }
    68→
    69→  // Find a summary whose leafUuid exists in this file (not cross-contaminated)
    70→  for (const s of summaries) {
    71→    if (s.leafUuid && uuids.has(s.leafUuid)) {
    72→      return s.summary;
    73→    }
    74→  }
    75→
    76→  // Fallback: return the last summary if no valid one found
    77→  return summaries.at(-1)?.summary;
    78→}
    79→
    80→interface ExtractSessionOptions {
    81→  rawPath: string;
    82→  outputPath: string;
    83→  agentId?: string;
    84→}
    85→
    86→export async function extractSession(options: ExtractSessionOptions) {
    87→  const { rawPath, outputPath, agentId } = options;
    88→
    89→  // outputPath is <cwd>/.claude/hive-mind/sessions/<id>.jsonl
    90→  // hiveMindDir is <cwd>/.claude/hive-mind
    91→  const hiveMindDir = dirname(dirname(outputPath));
    92→
    93→  const [content, rawStat, checkoutId] = await Promise.all([
    94→    readFile(rawPath, "utf-8"),
    95→    stat(rawPath),
    96→    getCheckoutId(hiveMindDir),
    97→  ]);
    98→
    99→  const t0Parse = process.env.DEBUG ? performance.now() : 0;
   100→  const entries: Array<ExtractedEntry> = [];
   101→
   102→  for (const rawEntry of parseJsonl(content)) {
   103→    const transformed = transformEntry(rawEntry);
   104→    if (transformed) {
   105→      entries.push(transformed);
   106→    }
   107→  }
   108→  if (process.env.DEBUG) {
   109→    console.log(`[extract] Parsing: ${(performance.now() - t0Parse).toFixed(2)}ms for ${entries.length} entries`);
   110→  }
   111→
   112→  // Skip sessions with no assistant messages
   113→  const hasAssistantMessage = entries.some((e) => e.type === "assistant");
   114→  if (!hasAssistantMessage) {
   115→    return null;
   116→  }
   117→
   118→  // For agent sessions, extract parentSessionId from first entry with sessionId
   119→  const parentSessionId = agentId
   120→    ? entries.find((e): e is ExtractedEntry & { sessionId: string } => "sessionId" in e && typeof e.sessionId === "string")?.sessionId
   121→    : undefined;
   122→
   123→  const summary = findValidSummary(entries);
   124→  const sessionId = basename(rawPath, ".jsonl");
   125→
   126→  const meta: HiveMindMeta = {
   127→    _type: "hive-mind-meta",
   128→    version: HIVE_MIND_VERSION,
   129→    sessionId,
   130→    checkoutId,
   131→    extractedAt: new Date().toISOString(),
   132→    rawMtime: rawStat.mtime.toISOString(),
   133→    messageCount: entries.length,
   134→    summary,
   135→    rawPath,
   136→    ...(agentId && { agentId }),
   137→    ...(parentSessionId && { parentSessionId }),
   138→  };
   139→
   140→  resetDetectSecretsStats();
   141→  const t0 = performance.now();
   142→  const sanitizedEntries = entries.map((e) => sanitizeDeep(e));
   143→  const sanitizeMs = performance.now() - t0;
   144→  if (process.env.DEBUG) {
   145→    const stats = getDetectSecretsStats();
   146→    console.log(`[extract] Sanitization: ${sanitizeMs.toFixed(2)}ms for ${entries.length} entries | detectSecrets: ${stats.calls} calls, ${stats.keywordHits} keyword hits, ${stats.regexRuns} regex runs, ${stats.totalMs.toFixed(2)}ms`);
   147→  }
   148→
   149→  // Write output
   150→  await mkdir(dirname(outputPath), { recursive: true });
   151→
   152→  const lines = [
   153→    JSON.stringify(meta),
   154→    ...sanitizedEntries.map((e) => JSON.stringify(e)),
   155→  ];
   156→
   157→  await writeFile(outputPath, `${lines.join("\n")}\n`);
   158→
   159→  return { messageCount: entries.length, summary };
   160→}
   161→
   162→async function readFirstLine(filePath: string): Promise<string | null> {
   163→  const stream = createReadStream(filePath, { encoding: "utf-8" });
   164→  const rl = createInterface({ input: stream, crlfDelay: Infinity });
   165→
   166→  try {
   167→    for await (const line of rl) {
   168→      stream.destroy();
   169→      return line;
   170→    }
   171→    return null;
   172→  } finally {
   173→    rl.close();
   174→  }
   175→}
   176→
   177→async function readExtractedMeta(
   178→  extractedPath: string,
   179→): Promise<HiveMindMeta | null> {
   180→  try {
   181→    const firstLine = await readFirstLine(extractedPath);
   182→    if (!firstLine) return null;
   183→
   184→    const parsed = HiveMindMetaSchema.safeParse(JSON.parse(firstLine));
   185→    return parsed.success ? parsed.data : null;
   186→  } catch {
   187→    return null;
   188→  }
   189→}
   190→
   191→function getProjectsDir(cwd: string): string {
   192→  // Encode cwd: replace / with -
   193→  const encoded = cwd.replace(/\//g, "-");
   194→  return join(homedir(), ".claude", "projects", encoded);
   195→}
   196→
   197→function getHiveMindSessionsDir(projectCwd: string): string {
   198→  return join(projectCwd, ".claude", "hive-mind", "sessions");
   199→}
   200→
   201→/** Returns both regular sessions and agent sessions. */
   202→async function findRawSessions(rawDir: string) {
   203→  try {
   204→    const files = await readdir(rawDir);
   205→    const sessions: Array<{ path: string; agentId?: string }> = [];
   206→
   207→    for (const f of files) {
   208→      if (!f.endsWith(".jsonl")) continue;
   209→
   210→      if (f.startsWith("agent-")) {
   211→        // Agent session: agent-<agentId>.jsonl
   212→        const agentId = f.replace("agent-", "").replace(".jsonl", "");
   213→        sessions.push({ path: join(rawDir, f), agentId });
   214→      } else {
   215→        // Regular session
   216→        sessions.push({ path: join(rawDir, f) });
   217→      }
   218→    }
   219→
   220→    return sessions;
   221→  } catch {
   222→    return [];
   223→  }
   224→}
   225→
   226→async function needsExtraction(
   227→  rawPath: string,
   228→  extractedPath: string,
   229→): Promise<boolean> {
   230→  try {
   231→    const rawStat = await stat(rawPath);
   232→    const meta = await readExtractedMeta(extractedPath);
   233→
   234→    if (!meta) return true;
   235→
   236→    // Compare mtimes
   237→    const rawMtime = rawStat.mtime.toISOString();
   238→    return rawMtime !== meta.rawMtime;
   239→  } catch {
   240→    return true;
   241→  }
   242→}
   243→
   244→export async function extractAllSessions(cwd: string, transcriptPath?: string) {
   245→  // Determine raw sessions directory
   246→  const rawDir = transcriptPath ? dirname(transcriptPath) : getProjectsDir(cwd);
   247→  const extractedDir = getHiveMindSessionsDir(cwd);
   248→
   249→  const rawSessions = await findRawSessions(rawDir);
   250→  let extracted = 0;
   251→
   252→  for (const session of rawSessions) {
   253→    const { path: rawPath, agentId } = session;
   254→
   255→    // Use same filename as original (agent-<id>.jsonl or <sessionId>.jsonl)
   256→    const extractedPath = join(extractedDir, basename(rawPath));
   257→
   258→    if (await needsExtraction(rawPath, extractedPath)) {
   259→      try {
   260→        const result = await extractSession({ rawPath, outputPath: extractedPath, agentId });
   261→        if (result) {
   262→          extracted++;
   263→        }
   264→      } catch (error) {
   265→        // Log but continue with other sessions
   266→        const id = basename(rawPath, ".jsonl");
   267→        console.error(`Failed to extract ${id}:`, error);
   268→      }
   269→    }
   270→  }
   271→
   272→  return extracted;
   273→}
