   122→describe("content block transformation", () => {
   123→  test("strips base64 data from image blocks", async () => {
   124→    const { extractSession } = await import("./extraction");
   125→    const { mkdtemp, writeFile, readFile, rm } = await import(
   126→      "node:fs/promises"
   127→    );
   128→    const { join } = await import("node:path");
   129→    const { tmpdir } = await import("node:os");
   130→
   131→    const tempDir = await mkdtemp(join(tmpdir(), "hive-test-"));
   132→    const rawPath = join(tempDir, "test-session.jsonl");
   133→    const outPath = join(tempDir, "out", "test-session.jsonl");
   134→
   135→    try {
   136→      // Create a fake base64 image (100 chars = ~75 bytes decoded)
   137→      const fakeBase64 = "A".repeat(100);
   138→      const userEntry = {
   139→        type: "user",
   140→        uuid: "1",
   141→        parentUuid: null,
   142→        timestamp: "2025-01-01",
   143→        message: {
   144→          role: "user",
   145→          content: [
   146→            {
   147→              type: "image",
   148→              source: {
   149→                type: "base64",
   150→                media_type: "image/png",
   151→                data: fakeBase64,
   152→              },
   153→            },
   154→            { type: "text", text: "here is an image" },
   155→          ],
   156→        },
   157→      };
   158→
   159→      const assistantEntry = {
   160→        type: "assistant",
   161→        uuid: "2",
   162→        parentUuid: "1",
   163→        timestamp: "2025-01-01",
   164→        message: { role: "assistant", content: "I see the image" },
   165→      };
   166→
   167→      await writeFile(rawPath, [JSON.stringify(userEntry), JSON.stringify(assistantEntry)].join("\n"));
   168→      await extractSession({ rawPath, outputPath: outPath });
   169→
   170→      const output = await readFile(outPath, "utf-8");
   171→      const lines = output.trim().split("\n");
   172→      const extracted = JSON.parse(lines[1]);
   173→
   174→      const imageBlock = extracted.message.content[0];
   175→      expect(imageBlock.type).toBe("image");
   176→      expect(imageBlock.source.type).toBe("base64");
   177→      expect(imageBlock.source.media_type).toBe("image/png");
   178→      expect(imageBlock.source.data).toBeUndefined();
   179→
   180→      // Text block should be unchanged
   181→      expect(extracted.message.content[1]).toEqual({
   182→        type: "text",
   183→        text: "here is an image",
   184→      });
   185→    } finally {
   186→      await rm(tempDir, { recursive: true });
   187→    }
   188→  });
