import { readdir, readFile } from "node:fs/promises";
import { join } from "node:path";
import { KnownEntrySchema } from "/Users/yoav/projects/alignment-hive/hive-mind/cli/lib/schemas";

const rawDir = "/Users/yoav/.claude/projects/-Users-yoav-projects-alignment-hive";

async function main() {
  const files = await readdir(rawDir);
  const jsonlFiles = files.filter(f => f.endsWith(".jsonl"));

  const samplesByError: Map<string, unknown> = new Map();

  for (const file of jsonlFiles) {
    const content = await readFile(join(rawDir, file), "utf-8");
    const lines = content.split("\n");

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();
      if (!line) continue;

      try {
        const parsed = JSON.parse(line);
        const result = KnownEntrySchema.safeParse(parsed);

        if (!result.success) {
          const errorKey = result.error.issues.map(e => {
            const path = e.path.join(".");
            return path + ": " + e.message;
          }).join("; ");

          if (!samplesByError.has(errorKey)) {
            samplesByError.set(errorKey, parsed);
          }
        }
      } catch (e) {
        // skip JSON parse errors
      }
    }
  }

  // Print samples
  for (const [error, sample] of samplesByError.entries()) {
    console.log("// Error: " + error);
    console.log(JSON.stringify(sample));
    console.log("");
  }
}

main().catch(console.error);

