describe("schema completeness", () => {
  test("all known entry types in raw files must be parseable", async () => {
    const { readdir, readFile } = await import("node:fs/promises");
    const { join } = await import("node:path");
    const { KnownEntrySchema } = await import("./schemas");

    const rawDir =
      "/Users/yoav/.claude/projects/-Users-yoav-projects-alignment-hive";

    let files: string[];
    try {
      files = await readdir(rawDir);
    } catch {
      // Skip test if raw directory doesn't exist
      console.log("Skipping test - raw session directory not found");
      return;
    }

    const jsonlFiles = files.filter((f) => f.endsWith(".jsonl")).slice(0, 5);
    const failures: Array<{
      file: string;
      line: number;
      type: string;
      error: string;
    }> = [];

    for (const file of jsonlFiles) {
      const content = await readFile(join(rawDir, file), "utf-8");
      const lines = content.split("\n");

      for (let i = 0; i < lines.length; i++) {
        const line = lines[i].trim();
        if (!line) continue;

        try {
          const parsed = JSON.parse(line);
          const entryType = parsed?.type;

          // Only check known entry types (not future unknown types)
          const knownTypes = [
            "user",
            "assistant",
            "summary",
            "system",
            "file-history-snapshot",
            "queue-operation",
          ];
          if (!knownTypes.includes(entryType)) continue;

          const result = KnownEntrySchema.safeParse(parsed);

          if (!result.success) {
            failures.push({
              file,
              line: i + 1,
              type: entryType,
              error: result.error.issues
                .map((e) => `${e.path.join(".")}: ${e.message}`)
                .join("; "),
            });
          }
        } catch {
          // Skip JSON parse errors - not a schema issue
        }
      }
    }

    // Report all failures for debugging
    if (failures.length > 0) {
      const byError = new Map<string, number>();
      for (const f of failures) {
        const key = `${f.type}: ${f.error}`;
        byError.set(key, (byError.get(key) || 0) + 1);
      }
      console.log("Schema failures by type:");
      for (const [key, count] of byError.entries()) {
        console.log(`  ${count}x ${key}`);
      }
    }

    expect(failures).toHaveLength(0);
  });

  test("stop_reason can be null", async () => {
    const { KnownEntrySchema } = await import("./schemas");

    const entry = {
      type: "assistant",
      uuid: "test-uuid",
      parentUuid: "parent-uuid",
      timestamp: "2025-01-01T00:00:00Z",
      message: {
        role: "assistant",
        content: [{ type: "text", text: "test" }],
        stop_reason: null,
      },
    };

    const result = KnownEntrySchema.safeParse(entry);
    expect(result.success).toBe(true);
  });

  test("imagePasteIds can contain numbers", async () => {
    const { KnownEntrySchema } = await import("./schemas");

    const entry = {
      type: "user",
      uuid: "test-uuid",
      parentUuid: null,
      timestamp: "2025-01-01T00:00:00Z",
      message: { role: "user", content: "test" },
      imagePasteIds: [123, 456],
    };

    const result = KnownEntrySchema.safeParse(entry);
    expect(result.success).toBe(true);
  });
});

