export function parseKnownEntry(data: unknown): KnownEntry | null {
  const parsed = KnownEntrySchema.safeParse(data);
  if (parsed.success) {
    return parsed.data;
  }

  // Check if this is a known type that failed to parse (schema bug)
  const entryType = (data as { type?: unknown })?.type;
  if (isKnownEntryType(entryType)) {
    // Known type failed to parse - this is a schema bug, fail loudly
    const errorDetails = parsed.error.issues
      .map((e) => `${e.path.join(".")}: ${e.message}`)
      .join("; ");
    throw new Error(
      `Schema bug: failed to parse known entry type "${entryType}": ${errorDetails}`,
    );
  }

  // Unknown entry type - skip gracefully for forward compatibility
  return null;
}

