import { z } from "zod";

/**
 * SCHEMA DESIGN CONSTRAINTS:
 *
 * 1. Self-compatibility: Transformed output must be parseable by the same schema.
 *    This is achieved by making all stripped fields optional in the input schema.
 *    This allows the same schemas to be used for both extraction and retrieval.
 *
 * 2. Field stripping: We strip fields that are:
 *    - Large (base64 data, full file contents)
 *    - Low retrieval value (internal IDs, redundant data)
 *    - Potentially sensitive (we keep file paths but strip file contents)
 *
 * 3. Fields we keep even if small: slug, usage, todos - useful for analysis
 */

export const TextBlockSchema = z.looseObject({
  type: z.literal("text"),
  text: z.string(),
});

export const ThinkingBlockSchema = z.looseObject({
  type: z.literal("thinking"),
  thinking: z.string(),
});

export const ToolUseBlockSchema = z.looseObject({
  type: z.literal("tool_use"),
  id: z.string(),
  name: z.string(),
  input: z.record(z.string(), z.unknown()),
});

// Tool results contain text/image content, never nested tool results
// Content is string or array of simple content blocks (text, image, document)
export const ToolResultContentBlockSchema = z.union([
  TextBlockSchema,
  z.looseObject({ type: z.literal("image") }),
  z.looseObject({ type: z.literal("document") }),
  z.looseObject({ type: z.string() }), // Unknown content types
]);

export const ToolResultBlockSchema = z.looseObject({
  type: z.literal("tool_result"),
  tool_use_id: z.string(),
  content: z.union([z.string(), z.array(ToolResultContentBlockSchema)]).optional(),
});

// Strip base64 data, keep metadata. Data is optional for self-compatibility.
const Base64SourceSchema = z
  .looseObject({
    type: z.literal("base64"),
    media_type: z.string(),
    data: z.string().optional(),
  })
  .transform(({ data, ...rest }) => rest);

// Transform strips low-value fields:
// - toolUseResult: redundant with message.content tool_result blocks
// - requestId: internal API ID
// - userType: always "external"
// - imagePasteIds: useless without images
// - thinkingMetadata: low value
// Keeps: slug (session name), todos (task progress)
export const UserEntrySchema = z
  .looseObject({
    type: z.literal("user"),
    uuid: z.string(),
    parentUuid: z.string().nullable(),
    timestamp: z.string(),
    sessionId: z.string().optional(),
    cwd: z.string().optional(),
    gitBranch: z.string().optional(),
    version: z.string().optional(),
    message: UserMessageObjectSchema,
    sourceToolUseID: z.string().optional(),
    slug: z.string().optional(), // Keep: human-readable session name
    todos: z.unknown().optional(), // Keep: task progress tracking
    // Optional for self-compatibility (stripped on transform)
    toolUseResult: z.unknown().optional(),
    requestId: z.string().optional(),
    userType: z.string().optional(),
    imagePasteIds: z.array(z.string()).optional(),
    thinkingMetadata: z.unknown().optional(),
  })
  .transform(({ toolUseResult, requestId, userType, imagePasteIds, thinkingMetadata, ...rest }) => rest);

