      expect(extracted.slug).toBeUndefined();
      expect(extracted.userType).toBeUndefined();
      expect(extracted.message.id).toBeUndefined();
      expect(extracted.message.usage).toBeUndefined();
    } finally {
      await rm(tempDir, { recursive: true });
    }
  });
});

