     1→/**
     2→ * Index command - list extracted sessions.
     3→ * Agent sessions excluded - explore via Task tool calls in parent sessions.
     4→ */
     5→
     6→import { readdir } from "node:fs/promises";
     7→import { join } from "node:path";
     8→import { getHiveMindSessionsDir, readExtractedSession } from "../lib/extraction";
     9→import { printError } from "../lib/output";
    10→import type { ContentBlock, HiveMindMeta, KnownEntry } from "../lib/schemas";
    11→
    12→interface SessionInfo {
    13→  meta: HiveMindMeta;
    14→  entries: Array<KnownEntry>;
    15→}
    16→
    17→export async function index(): Promise<void> {
    18→  const cwd = process.cwd();
    19→  const sessionsDir = getHiveMindSessionsDir(cwd);
    20→
    21→  let files: Array<string>;
    22→  try {
    23→    files = await readdir(sessionsDir);
    24→  } catch {
    25→    printError(`No sessions found. Run 'extract' first.`);
    26→    return;
    27→  }
    28→
    29→  const jsonlFiles = files.filter((f) => f.endsWith(".jsonl"));
    30→  if (jsonlFiles.length === 0) {
    31→    printError(`No sessions found in ${sessionsDir}`);
    32→    return;
    33→  }
    34→
    35→  const sessions: Array<SessionInfo> = [];
    36→  for (const file of jsonlFiles) {
    37→    const path = join(sessionsDir, file);
    38→    const session = await readExtractedSession(path);
    39→    if (session && !session.meta.agentId) {
    40→      sessions.push(session);
    41→    }
    42→  }
    43→
    44→  sessions.sort((a, b) => b.meta.rawMtime.localeCompare(a.meta.rawMtime));
    45→
    46→  console.log("ID\tDATETIME\tMSGS\tSUMMARY\tCOMMITS");
    47→  for (const session of sessions) {
    48→    console.log(formatSessionLine(session));
    49→  }
    50→}
    51→
    52→function formatSessionLine(session: SessionInfo): string {
    53→  const { meta, entries } = session;
    54→  const id = meta.sessionId.slice(0, 16);
    55→  const datetime = meta.rawMtime.slice(0, 16);
    56→  const count = String(meta.messageCount);
    57→  const summary = findSummary(entries) || findFirstUserPrompt(entries) || "";
    58→
    59→  const commits = findGitCommits(entries).filter((c) => c.success);
    60→  // Show hashes (agent can use git show for details) with fallback to truncated message
    61→  const commitList = commits.map((c) =>
    62→    c.hash || (c.message.length > 50 ? `${c.message.slice(0, 47)}...` : c.message)
    63→  ).join(" ");
    64→
    65→  return `${id}\t${datetime}\t${count}\t${summary}\t${commitList}`;
    66→}
    67→
    68→function findSummary(entries: Array<KnownEntry>): string | undefined {
    69→  const uuids = new Set<string>();
    70→  const summaries: Array<{ summary: string; leafUuid?: string }> = [];
    71→
    72→  for (const entry of entries) {
    73→    if ("uuid" in entry && typeof entry.uuid === "string") {
    74→      uuids.add(entry.uuid);
    75→    }
    76→    if (entry.type === "summary") {
    77→      summaries.push({ summary: entry.summary, leafUuid: entry.leafUuid });
    78→    }
    79→  }
    80→
    81→  for (const s of summaries) {
    82→    if (s.leafUuid && uuids.has(s.leafUuid)) {
    83→      return s.summary;
    84→    }
    85→  }
    86→
    87→  return summaries.at(-1)?.summary;
    88→}
    89→
    90→function findFirstUserPrompt(entries: Array<KnownEntry>): string | undefined {
    91→  for (const entry of entries) {
    92→    if (entry.type !== "user") continue;
    93→    const content = entry.message.content;
    94→    if (!content) continue;
    95→
    96→    let text: string | undefined;
    97→    if (typeof content === "string") {
    98→      text = content;
    99→    } else if (Array.isArray(content)) {
   100→      for (const block of content) {
   101→        if (block.type === "text" && "text" in block && typeof block.text === "string") {
   102→          text = block.text;
   103→          break;
   104→        }
   105→      }
   106→    }
   107→
   108→    if (text) {
   109→      const firstLine = text.split("\n")[0].trim();
   110→      if (firstLine) {
   111→        return firstLine.length > 100 ? `${firstLine.slice(0, 97)}...` : firstLine;
   112→      }
   113→    }
   114→  }
   115→  return undefined;
   116→}
   117→
   118→interface GitCommit {
   119→  hash: string | undefined;
   120→  message: string;
   121→  success: boolean;
   122→}
   123→
   124→function findGitCommits(entries: Array<KnownEntry>): Array<GitCommit> {
   125→  const commits: Array<GitCommit> = [];
   126→  const pendingCommits = new Map<string, string>();
   127→
   128→  for (const entry of entries) {
   129→    if (entry.type === "assistant") {
   130→      const content = entry.message.content;
   131→      if (!Array.isArray(content)) continue;
   132→
   133→      for (const block of content) {
   134→        if (block.type === "tool_use" && "name" in block && block.name === "Bash") {
   135→          const input = block.input;
   136→          const command = input.command;
   137→          if (typeof command === "string" && command.includes("git commit")) {
   138→            const message = extractCommitMessage(command);
   139→            if (message && "id" in block && typeof block.id === "string") {
   140→              pendingCommits.set(block.id, message);
   141→            }
   142→          }
   143→        }
   144→      }
   145→    } else if (entry.type === "user") {
   146→      const content = entry.message.content;
   147→      if (!Array.isArray(content)) continue;
   148→
   149→      for (const block of content) {
   150→        if (block.type === "tool_result" && "tool_use_id" in block) {
   151→          const toolUseId = block.tool_use_id;
   152→          const message = pendingCommits.get(toolUseId);
   153→          if (message) {
   154→            const resultContent = getToolResultText(block.content as string | Array<ContentBlock> | undefined);
   155→            const success = resultContent.includes("[") && !resultContent.includes("error");
   156→            const hash = extractCommitHash(resultContent);
   157→            commits.push({ hash, message, success });
   158→            pendingCommits.delete(toolUseId);
   159→          }
   160→        }
   161→      }
   162→    }
   163→  }
   164→
   165→  for (const message of pendingCommits.values()) {
   166→    commits.push({ hash: undefined, message, success: true });
   167→  }
   168→
   169→  return commits;
   170→}
   171→
   172→function extractCommitHash(output: string): string | undefined {
   173→  // Parse "[branch abc1234] message" format
   174→  const match = output.match(/\[[\w/-]+\s+([a-f0-9]{7,})\]/);
   175→  return match?.[1];
   176→}
   177→
   178→function extractCommitMessage(command: string): string | undefined {
   179→  // Heredoc: -m "$(cat <<'EOF'\nmessage\nEOF\n)"
   180→  const heredocMatch = command.match(/<<['"]?EOF['"]?\s*\n([\s\S]*?)\n\s*EOF/);
   181→  if (heredocMatch) {
   182→    const firstLine = heredocMatch[1].trim().split("\n")[0].trim();
   183→    if (firstLine) return firstLine;
   184→  }
   185→
   186→  // -m "message" (not heredoc)
   187→  const mFlagMatch = command.match(/git commit[^"']*-m\s*["'](?!\$\()([^"']+)["']/);
   188→  if (mFlagMatch) return mFlagMatch[1].trim();
   189→
   190→  // Simple -m message (no quotes)
   191→  const simpleMatch = command.match(/git commit[^-]*-m\s+(\S+)/);
   192→  if (simpleMatch && !simpleMatch[1].startsWith('"') && !simpleMatch[1].startsWith("'")) {
   193→    return simpleMatch[1];
   194→  }
   195→
   196→  return undefined;
   197→}
   198→
   199→function getToolResultText(content: string | Array<ContentBlock> | undefined): string {
   200→  if (!content) return "";
   201→  if (typeof content === "string") return content;
   202→
   203→  const parts: Array<string> = [];
   204→  for (const block of content) {
   205→    if (block.type === "text" && "text" in block) {
   206→      parts.push(block.text);
   207→    }
   208→  }
   209→  return parts.join("\n");
   210→}
