     1→/**
     2→ * Read command - read session entries.
     3→ *
     4→ * Usage:
     5→ *   read <session-id>        - all entries (truncated for scanning)
     6→ *   read <session-id> N      - entry N (full content)
     7→ *   read <session-id> --full - all entries (full content)
     8→ *
     9→ * Session ID supports prefix matching (e.g., "02ed" matches "02ed589a-...")
    10→ *
    11→ * Truncation: When reading all entries, multi-line content is truncated to first
    12→ * line + line count (e.g., "[+42 lines]"). This allows quick scanning of session
    13→ * content. Request a specific entry number to see full content, or pipe through
    14→ * `head -n 50` to control output length.
    15→ */
    16→
    17→import { readFile, readdir } from "node:fs/promises";
    18→import { join } from "node:path";
    19→import { getHiveMindSessionsDir, parseJsonl } from "../lib/extraction";
    20→import { collectToolResults, formatEntry, formatSession, getLogicalEntries } from "../lib/format";
    21→import { printError } from "../lib/output";
    22→import { parseKnownEntry } from "../lib/schemas";
    23→import type { KnownEntry } from "../lib/schemas";
    24→
    25→export async function read(): Promise<void> {
    26→  const args = process.argv.slice(3);
    27→
    28→  if (args.length === 0) {
    29→    printError("Usage: read <session-id> [N] [--full]");
    30→    console.log("\nExamples:");
    31→    console.log("  read 02ed        # all entries (truncated for scanning)");
    32→    console.log("  read 02ed --full # all entries (full content)");
    33→    console.log("  read 02ed 5      # entry 5 (full content)");
    34→    return;
    35→  }
    36→
    37→  // Parse args: session-id, optional entry number, optional --full flag
    38→  const fullFlag = args.includes("--full");
    39→  const filteredArgs = args.filter((a) => a !== "--full");
    40→  const sessionIdPrefix = filteredArgs[0];
    41→  const entryArg = filteredArgs[1];
    42→
    43→  const cwd = process.cwd();
    44→  const sessionsDir = getHiveMindSessionsDir(cwd);
    45→
    46→  // Find matching session file
    47→  let files: Array<string>;
    48→  try {
    49→    files = await readdir(sessionsDir);
    50→  } catch {
    51→    printError(`No sessions found. Run 'extract' first.`);
    52→    return;
    53→  }
    54→
    55→  const jsonlFiles = files.filter((f) => f.endsWith(".jsonl"));
    56→  const matches = jsonlFiles.filter((f) => {
    57→    const name = f.replace(".jsonl", "");
    58→    return name.startsWith(sessionIdPrefix) || name === `agent-${sessionIdPrefix}`;
    59→  });
    60→
    61→  if (matches.length === 0) {
    62→    printError(`No session found matching '${sessionIdPrefix}'`);
    63→    return;
    64→  }
    65→
    66→  if (matches.length > 1) {
    67→    printError(`Multiple sessions match '${sessionIdPrefix}':`);
    68→    for (const m of matches.slice(0, 5)) {
    69→      console.log(`  ${m.replace(".jsonl", "")}`);
    70→    }
    71→    if (matches.length > 5) {
    72→      console.log(`  ... and ${matches.length - 5} more`);
    73→    }
    74→    return;
    75→  }
    76→
    77→  const sessionFile = join(sessionsDir, matches[0]);
    78→
    79→  // Parse entry number (if provided)
    80→  let entryNumber: number | null = null;
    81→  if (entryArg) {
    82→    entryNumber = parseInt(entryArg, 10);
    83→    if (isNaN(entryNumber) || entryNumber < 1) {
    84→      printError(`Invalid entry number: ${entryArg}`);
    85→      return;
    86→    }
    87→  }
    88→
    89→  // Read and parse session
    90→  const content = await readFile(sessionFile, "utf-8");
    91→  const lines = Array.from(parseJsonl(content));
    92→
    93→  // Skip metadata (line 0), entries start at line 1
    94→  // User-facing indices are 1-indexed (entry 1 = lines[1])
    95→  const rawEntries = lines.slice(1);
    96→
    97→  if (rawEntries.length === 0) {
    98→    printError("Session has no entries.");
    99→    return;
   100→  }
   101→
   102→  // Parse all entries
   103→  const allEntries: Array<KnownEntry> = [];
   104→  for (const raw of rawEntries) {
   105→    const result = parseKnownEntry(raw);
   106→    if (result.data) {
   107→      allEntries.push(result.data);
   108→    }
   109→  }
   110→
   111→  // Build logical entry mapping (matches line numbers shown in formatSession)
   112→  const logicalEntries = getLogicalEntries(allEntries);
   113→  // Collect tool results for merging with tool_use entries
   114→  const toolResults = collectToolResults(allEntries);
   115→
   116→  if (entryNumber === null) {
   117→    // All entries mode: use formatSession
   118→    // Truncate unless --full flag is set
   119→    const redact = !fullFlag;
   120→    const output = formatSession(allEntries, { redact });
   121→    console.log(output);
   122→  } else {
   123→    // Single entry mode: find entry by logical line number
   124→    const logicalEntry = logicalEntries.find((e) => e.lineNumber === entryNumber);
   125→    if (!logicalEntry) {
   126→      const maxLine = logicalEntries.length > 0 ? logicalEntries[logicalEntries.length - 1].lineNumber : 0;
   127→      printError(`Entry ${entryNumber} not found (session has ${maxLine} entries)`);
   128→      return;
   129→    }
   130→
   131→    const formatted = formatEntry(logicalEntry.entry, {
   132→      lineNumber: entryNumber,
   133→      redact: false,
   134→      toolResults,
   135→    });
   136→    if (formatted) {
   137→      console.log(formatted);
   138→    }
   139→  }
   140→}
