   139→  try {
   140→    const content = await readFile(extractedPath, "utf-8");
   141→    const lines = content.split("\n").filter((l) => l.trim());
   142→    if (lines.length === 0) return null;
   143→
   144→    const metaParsed = HiveMindMetaSchema.safeParse(JSON.parse(lines[0]));
   145→    if (!metaParsed.success) return null;
   146→
   147→    const entries: Array<KnownEntry> = [];
   148→    for (let i = 1; i < lines.length; i++) {
