    14→export function* parseJsonl(content: string) {
    15→  for (const line of content.split("\n")) {
    16→    const trimmed = line.trim();
    17→    if (!trimmed) continue;
    18→    try {
    19→      yield JSON.parse(trimmed) as unknown;
    20→    } catch (error) {
    21→      if (process.env.DEBUG) {
    22→        console.warn("Skipping malformed JSONL line:", error);
