    30→function transformEntry(rawEntry: unknown): { entry: ExtractedEntry | null; error?: string } {
    31→  const result = parseKnownEntry(rawEntry);
    32→  if (result.error) return { entry: null, error: result.error };
    33→  if (!result.data) return { entry: null };
    34→
    35→  if (INCLUDED_ENTRY_TYPES.includes(result.data.type as (typeof INCLUDED_ENTRY_TYPES)[number])) {
    36→    return { entry: result.data as ExtractedEntry };
    37→  }
    38→  return { entry: null };
