     1→import { createRouter } from '@tanstack/react-router';
     2→import { ConvexQueryClient } from '@convex-dev/react-query';
     3→import { QueryClient } from '@tanstack/react-query';
     4→import { setupRouterSsrQueryIntegration } from '@tanstack/react-router-ssr-query';
     5→import { ConvexProvider, ConvexProviderWithAuth, ConvexReactClient } from 'convex/react';
     6→import { AuthKitProvider, useAccessToken, useAuth } from '@workos/authkit-tanstack-react-start/client';
     7→import { useCallback, useMemo } from 'react';
     8→import { routeTree } from './routeTree.gen';
     9→
    10→export function getRouter() {
    11→  const CONVEX_URL = (import.meta as any).env.VITE_CONVEX_URL!;
    12→  if (!CONVEX_URL) {
    13→    throw new Error('missing VITE_CONVEX_URL env var');
    14→  }
    15→  const convex = new ConvexReactClient(CONVEX_URL);
    16→  const convexQueryClient = new ConvexQueryClient(convex);
    17→
    18→  const queryClient = new QueryClient({
    19→    defaultOptions: {
    20→      queries: {
    21→        queryKeyHashFn: convexQueryClient.hashFn(),
    22→        queryFn: convexQueryClient.queryFn(),
    23→        gcTime: 5000,
    24→      },
    25→    },
    26→  });
    27→  convexQueryClient.connect(queryClient);
    28→
    29→  const router = createRouter({
    30→    routeTree,
    31→    defaultPreload: 'intent',
    32→    scrollRestoration: true,
    33→    defaultPreloadStaleTime: 0, // Let React Query handle all caching
    34→    defaultErrorComponent: (err) => <p>{err.error.stack}</p>,
    35→    defaultNotFoundComponent: () => <p>not found</p>,
    36→    context: { queryClient, convexClient: convex, convexQueryClient },
    37→    Wrap: ({ children }) => (
    38→      <AuthKitProvider>
    39→        <ConvexProviderWithAuth client={convexQueryClient.convexClient} useAuth={useAuthFromWorkOS}>
    40→          {children}
    41→        </ConvexProviderWithAuth>
    42→      </AuthKitProvider>
    43→    ),
    44→  });
    45→  setupRouterSsrQueryIntegration({ router, queryClient });
    46→
    47→  return router;
    48→}
    49→
    50→function useAuthFromWorkOS() {
    51→  const { loading, user } = useAuth();
    52→  const { accessToken, getAccessToken } = useAccessToken();
    53→
    54→  const fetchAccessToken = useCallback(
    55→    async ({ forceRefreshToken }: { forceRefreshToken: boolean }) => {
    56→      if (!accessToken || forceRefreshToken) {
    57→        return (await getAccessToken()) ?? null;
    58→      }
    59→
    60→      return accessToken;
    61→    },
    62→    [accessToken, getAccessToken],
    63→  );
    64→
    65→  return useMemo(
    66→    () => ({
    67→      isLoading: loading,
    68→      isAuthenticated: !!user,
    69→      fetchAccessToken,
    70→    }),
    71→    [loading, user, fetchAccessToken],
    72→  );
    73→}
