     1→import { HeadContent, Outlet, Scripts, createRootRouteWithContext } from '@tanstack/react-router';
     2→import { createServerFn } from '@tanstack/react-start';
     3→import { getAuth } from '@workos/authkit-tanstack-react-start';
     4→import appCssUrl from '../app.css?url';
     5→import type { QueryClient } from '@tanstack/react-query';
     6→import type { ReactNode } from 'react';
     7→import type { ConvexReactClient } from 'convex/react';
     8→import type { ConvexQueryClient } from '@convex-dev/react-query';
