    10→const fetchWorkosAuth = createServerFn({ method: 'GET' }).handler(async () => {
    11→  const auth = await getAuth();
    12→  const { user } = auth;
    13→
    14→  return {
    15→    userId: user?.id ?? null,
    16→    token: user ? auth.accessToken : null,
    17→  };
    18→});
    19→
    20→export const Route = createRootRouteWithContext<{
    21→  queryClient: QueryClient;
    22→  convexClient: ConvexReactClient;
    23→  convexQueryClient: ConvexQueryClient;
    24→}>()({
    25→  head: () => ({
    26→    meta: [
    27→      {
    28→        charSet: 'utf-8',
    29→      },
    30→      {
    31→        name: 'viewport',
    32→        content: 'width=device-width, initial-scale=1',
    33→      },
    34→      {
    35→        title: 'Convex + TanStack Start + WorkOS AuthKit',
    36→      },
    37→    ],
    38→    links: [
    39→      { rel: 'stylesheet', href: appCssUrl },
    40→      { rel: 'icon', href: '/convex.svg' },
    41→    ],
    42→  }),
    43→  component: RootComponent,
    44→  notFoundComponent: () => <div>Not Found</div>,
    45→  beforeLoad: async (ctx) => {
    46→    const { userId, token } = await fetchWorkosAuth();
    47→
    48→    // During SSR only (the only time serverHttpClient exists),
    49→    // set the Clerk auth token to make HTTP queries with.
    50→    if (token) {
    51→      ctx.context.convexQueryClient.serverHttpClient?.setAuth(token);
    52→    }
    53→
    54→    return { userId, token };
    55→  },
    56→});
    57→
    58→function RootComponent() {
    59→  return (
    60→    <RootDocument>
    61→      <Outlet />
    62→    </RootDocument>
    63→  );
    64→}
    65→
    66→function RootDocument({ children }: Readonly<{ children: ReactNode }>) {
    67→  return (
    68→    <html lang="en">
    69→      <head>
    70→        <HeadContent />
    71→      </head>
    72→      <body>
    73→        {children}
    74→        <Scripts />
    75→      </body>
    76→    </html>
    77→  );
    78→}
