     1→import { describe, expect, test } from "bun:test";
     2→import { detectSecrets, sanitizeDeep, sanitizeString } from "./sanitize";
     3→
     4→// Use high-entropy test tokens (repeated characters fail entropy checks)
     5→// GitHub PAT tokens are ghp_ + 36 alphanumeric chars
     6→const [REDACTED:generic-api-key];
     7→
     8→describe("detectSecrets", () => {
     9→  test("detects GitHub token", () => {
    10→    // GitHub personal access tokens (classic) start with ghp_
    11→    // When standalone, matches github-pat; with context, may match generic-api-key
    12→    const secrets = detectSecrets(TEST_GITHUB_TOKEN);
    13→    expect(secrets.length).toBeGreaterThan(0);
    14→    expect(secrets[0].ruleId).toBe("github-pat");
    15→  });
    16→
    17→  test("detects Slack webhook", async () => {
    18→    // Slack webhooks are reliably detected
    19→    const content =
    20→      "[REDACTED:slack-webhook-url]";
    21→    const secrets = await detectSecrets(content);
    22→    expect(secrets.length).toBeGreaterThan(0);
    23→  });
    24→
    25→  test("returns empty for clean content", async () => {
    26→    const content = "This is just normal text without any secrets.";
    27→    const secrets = await detectSecrets(content);
    28→    expect(secrets).toHaveLength(0);
    29→  });
    30→});
    31→
    32→describe("sanitizeString", () => {
