    38→  });
    39→
    40→  test("preserves non-secret content", () => {
    41→    const content = "Hello, this is a normal message.";
    42→    const sanitized = sanitizeString(content);
    43→    expect(sanitized).toBe(content);
    44→  });
    45→
    46→  test("handles token at end of string", () => {
    47→    const content = `my token is ${TEST_GITHUB_TOKEN}`;
    48→    const sanitized = sanitizeString(content);
    49→    expect(sanitized).not.toContain("ghp_");
    50→    expect(sanitized).toContain("[REDACTED:");
