    52→  });
    53→});
    54→
    55→const [REDACTED:generic-api-key];
    56→
    57→describe("sanitizeDeep", () => {
    58→  test("sanitizes strings in nested objects", async () => {
    59→    const input = {
    60→      level1: {
    61→        level2: {
    62→          secret: TEST_GITHUB_TOKEN,
    63→          normal: "hello",
    64→        },
    65→      },
    66→    };
    67→    const sanitized = await sanitizeDeep(input);
    68→    expect(sanitized.level1.level2.secret).not.toContain("ghp_");
    69→    expect(sanitized.level1.level2.secret).toContain("[REDACTED:");
    70→    expect(sanitized.level1.level2.normal).toBe("hello");
    71→  });
    72→
    73→  test("sanitizes strings in arrays", async () => {
    74→    const input = ["normal", TEST_GITHUB_TOKEN, "also normal"];
    75→    const sanitized = await sanitizeDeep(input);
    76→    expect(sanitized[0]).toBe("normal");
    77→    expect(sanitized[1]).toContain("[REDACTED:");
    78→    expect(sanitized[2]).toBe("also normal");
    79→  });
