     1→import { mkdir, readdir, readFile, stat, writeFile } from "node:fs/promises";
     2→import { homedir } from "node:os";
     3→import { basename, dirname, join } from "node:path";
     4→import { getMachineId } from "./config";
     5→import { sanitizeDeep } from "./sanitize";
     6→import {
     7→  type ContentBlock,
     8→  type HiveMindMeta,
     9→  HiveMindMetaSchema,
    10→  KnownContentBlockSchema,
    11→  parseKnownEntry,
    12→  type TransformedDocumentBlock,
    13→  type TransformedImageBlock,
    14→  type TransformedToolResultBlock,
    15→} from "./schemas";
    16→
    17→const HIVE_MIND_VERSION = "0.1";
    18→
    19→const SKIP_ENTRY_TYPES = new Set(["file-history-snapshot", "queue-operation"]);
    20→
    21→export function* parseJsonl(content: string): Generator<unknown> {
    22→  for (const line of content.split("\n")) {
    23→    const trimmed = line.trim();
    24→    if (!trimmed) continue;
    25→    try {
    26→      yield JSON.parse(trimmed);
    27→    } catch (error) {
    28→      // Skip malformed lines, log in debug mode
    29→      if (process.env.DEBUG) {
    30→        console.warn("Skipping malformed JSONL line:", error);
    31→      }
    32→    }
    33→  }
    34→}
    35→
    36→function isRecord(value: unknown): value is Record<string, unknown> {
    37→  return typeof value === "object" && value !== null && !Array.isArray(value);
    38→}
    39→
    40→function getBase64DecodedSize(base64: string): number {
    41→  if (!base64) return 0;
    42→  const paddingCount = (base64.match(/=+$/) || [""])[0].length;
    43→  return Math.floor(((base64.length - paddingCount) * 3) / 4);
    44→}
    45→
    46→function isContentBlock(value: unknown): value is ContentBlock {
    47→  return isRecord(value) && typeof value.type === "string";
    48→}
    49→
    50→function transformContentBlock(block: ContentBlock): ContentBlock | TransformedImageBlock | TransformedDocumentBlock | TransformedToolResultBlock {
    51→  const parsed = KnownContentBlockSchema.safeParse(block);
    52→  if (!parsed.success) return block;
    53→
    54→  const knownBlock = parsed.data;
    55→
    56→  if (knownBlock.type === "image" && knownBlock.source.type === "base64") {
    57→    return { type: "image", size: getBase64DecodedSize(knownBlock.source.data) };
    58→  }
    59→
    60→  if (knownBlock.type === "document" && knownBlock.source.type === "base64") {
    61→    return {
    62→      type: "document",
    63→      media_type: knownBlock.source.media_type,
    64→      size: getBase64DecodedSize(knownBlock.source.data),
    65→    };
    66→  }
    67→
    68→  if (knownBlock.type === "tool_result" && Array.isArray(knownBlock.content)) {
    69→    return {
    70→      type: "tool_result",
    71→      tool_use_id: knownBlock.tool_use_id,
    72→      content: knownBlock.content.map((c) => (isContentBlock(c) ? transformContentBlock(c) : c)),
    73→    };
    74→  }
    75→
    76→  return block;
    77→}
    78→
    79→function transformEntry(rawEntry: unknown): Record<string, unknown> | null {
    80→  const entry = parseKnownEntry(rawEntry);
    81→  if (!entry) {
    82→    if (process.env.DEBUG) {
    83→      console.warn("Skipping unknown entry type");
    84→    }
    85→    return null;
    86→  }
    87→
    88→  if (SKIP_ENTRY_TYPES.has(entry.type)) {
    89→    return null;
    90→  }
    91→
    92→  // Schema transforms already stripped low-value fields
    93→  // Here we only transform base64 content to size placeholders
    94→
    95→  if (entry.type === "user" || entry.type === "assistant") {
    96→    const { message, ...rest } = entry;
    97→    const content = message?.content;
    98→    const transformedContent =
    99→      content && Array.isArray(content) ? content.map(transformContentBlock) : content;
   100→
   101→    return {
   102→      ...rest,
   103→      message: { ...message, content: transformedContent },
   104→    };
   105→  }
   106→
   107→  if (entry.type === "summary" || entry.type === "system") {
   108→    return { ...entry };
   109→  }
   110→
   111→  return null;
   112→}
   113→
   114→/** Returns the summary where leafUuid exists in the same session. */
   115→function findValidSummary(
   116→  entries: Array<Record<string, unknown>>,
   117→): string | undefined {
   118→  const uuids = new Set<string>();
   119→  const summaries: Array<{ summary: string; leafUuid?: string }> = [];
   120→
   121→  for (const entry of entries) {
   122→    if (typeof entry.uuid === "string") {
   123→      uuids.add(entry.uuid);
   124→    }
   125→    if (entry.type === "summary" && typeof entry.summary === "string") {
   126→      summaries.push({
   127→        summary: entry.summary,
   128→        leafUuid: typeof entry.leafUuid === "string" ? entry.leafUuid : undefined,
   129→      });
   130→    }
   131→  }
   132→
   133→  // Find a summary whose leafUuid exists in this file (not cross-contaminated)
   134→  for (const s of summaries) {
   135→    if (s.leafUuid && uuids.has(s.leafUuid)) {
   136→      return s.summary;
   137→    }
   138→  }
   139→
   140→  // Fallback: return the last summary if no valid one found
   141→  if (summaries.length > 0) {
   142→    return summaries[summaries.length - 1].summary;
   143→  }
   144→
   145→  return undefined;
   146→}
