   148→interface ExtractSessionOptions {
   149→  rawPath: string;
   150→  outputPath: string;
   151→  agentId?: string;
   152→}
   153→
   154→export async function extractSession(options: ExtractSessionOptions) {
   155→  const { rawPath, outputPath, agentId } = options;
   156→
   157→  const [content, rawStat, machineId] = await Promise.all([
   158→    readFile(rawPath, "utf-8"),
   159→    stat(rawPath),
   160→    getMachineId(),
   161→  ]);
   162→
   163→  const entries: Array<Record<string, unknown>> = [];
   164→
   165→  // For agent sessions, extract parentSessionId from first entry
   166→  let parentSessionId: string | undefined;
   167→
   168→  for (const rawEntry of parseJsonl(content)) {
   169→    if (agentId && !parentSessionId && isRecord(rawEntry)) {
   170→      if (typeof rawEntry.sessionId === "string") {
   171→        parentSessionId = rawEntry.sessionId;
   172→      }
   173→    }
   174→
   175→    const transformed = transformEntry(rawEntry);
   176→    if (transformed) {
   177→      entries.push(transformed);
   178→    }
   179→  }
   180→
   181→  // Find valid summary
   182→  const summary = findValidSummary(entries);
   183→
   184→  // Determine session ID from filename (for agents, use agentId)
   185→  const sessionId = agentId || basename(rawPath, ".jsonl");
   186→
   187→  // Create metadata
   188→  const meta: HiveMindMeta = {
   189→    _type: "hive-mind-meta",
   190→    version: HIVE_MIND_VERSION,
   191→    sessionId,
   192→    machineId,
   193→    extractedAt: new Date().toISOString(),
   194→    rawMtime: rawStat.mtime.toISOString(),
   195→    messageCount: entries.length,
   196→    summary,
   197→    rawPath,
   198→    // Agent-specific fields
   199→    ...(agentId && { agentId }),
   200→    ...(parentSessionId && { parentSessionId }),
   201→  };
   202→
   203→  // Sanitize all entries
   204→  const sanitizedEntries = await Promise.all(
   205→    entries.map((e) => sanitizeDeep(e)),
   206→  );
   207→
   208→  // Write output
   209→  await mkdir(dirname(outputPath), { recursive: true });
   210→
   211→  const lines = [
   212→    JSON.stringify(meta),
   213→    ...sanitizedEntries.map((e) => JSON.stringify(e)),
   214→  ];
   215→
   216→  await writeFile(outputPath, `${lines.join("\n")}\n`);
   217→
   218→  return { messageCount: entries.length, summary };
   219→}
   220→
   221→export async function readExtractedMeta(
   222→  extractedPath: string,
   223→): Promise<HiveMindMeta | null> {
   224→  try {
   225→    const content = await readFile(extractedPath, "utf-8");
   226→    const firstLine = content.split("\n")[0];
   227→    if (!firstLine) return null;
   228→
   229→    const parsed = HiveMindMetaSchema.safeParse(JSON.parse(firstLine));
   230→    return parsed.success ? parsed.data : null;
   231→  } catch {
   232→    return null;
   233→  }
   234→}
   235→
   236→export function getProjectsDir(cwd: string): string {
   237→  // Encode cwd: replace / with -
   238→  const encoded = cwd.replace(/\//g, "-");
   239→  return join(homedir(), ".claude", "projects", encoded);
   240→}
   241→
   242→export function getHiveMindSessionsDir(projectCwd: string): string {
   243→  return join(projectCwd, ".claude", "hive-mind", "sessions");
   244→}
   245→
   246→/** Returns both regular sessions and agent sessions. */
   247→export async function findRawSessions(rawDir: string) {
   248→  try {
   249→    const files = await readdir(rawDir);
   250→    const sessions: Array<{ path: string; agentId?: string }> = [];
   251→
   252→    for (const f of files) {
   253→      if (!f.endsWith(".jsonl")) continue;
   254→
   255→      if (f.startsWith("agent-")) {
   256→        // Agent session: agent-<agentId>.jsonl
   257→        const agentId = f.replace("agent-", "").replace(".jsonl", "");
   258→        sessions.push({ path: join(rawDir, f), agentId });
   259→      } else {
   260→        // Regular session
   261→        sessions.push({ path: join(rawDir, f) });
   262→      }
   263→    }
   264→
   265→    return sessions;
   266→  } catch {
   267→    return [];
   268→  }
   269→}
   270→
   271→export async function needsExtraction(
   272→  rawPath: string,
   273→  extractedPath: string,
   274→): Promise<boolean> {
   275→  try {
   276→    const rawStat = await stat(rawPath);
   277→    const meta = await readExtractedMeta(extractedPath);
   278→
   279→    if (!meta) return true;
   280→
   281→    // Compare mtimes
   282→    const rawMtime = rawStat.mtime.toISOString();
   283→    return rawMtime !== meta.rawMtime;
   284→  } catch {
   285→    return true;
   286→  }
   287→}
   288→
   289→export async function extractAllSessions(cwd: string, transcriptPath?: string) {
   290→  // Determine raw sessions directory
   291→  const rawDir = transcriptPath ? dirname(transcriptPath) : getProjectsDir(cwd);
   292→  const extractedDir = getHiveMindSessionsDir(cwd);
   293→
   294→  const rawSessions = await findRawSessions(rawDir);
   295→  let extracted = 0;
   296→
   297→  for (const session of rawSessions) {
   298→    const { path: rawPath, agentId } = session;
   299→
   300→    // Use same filename as original (agent-<id>.jsonl or <sessionId>.jsonl)
   301→    const extractedPath = join(extractedDir, basename(rawPath));
   302→
   303→    if (await needsExtraction(rawPath, extractedPath)) {
   304→      try {
   305→        await extractSession({ rawPath, outputPath: extractedPath, agentId });
   306→        extracted++;
   307→      } catch (error) {
   308→        // Log but continue with other sessions
   309→        const id = agentId || basename(rawPath, ".jsonl");
   310→        console.error(`Failed to extract ${id}:`, error);
   311→      }
   312→    }
   313→  }
   314→
   315→  return extracted;
   316→}
