     3→export const TextBlockSchema = z.looseObject({
     4→  type: z.literal("text"),
     5→  text: z.string(),
     6→});
     7→
     8→export const ThinkingBlockSchema = z.looseObject({
     9→  type: z.literal("thinking"),
    10→  thinking: z.string(),
    11→});
    12→
    13→export const ToolUseBlockSchema = z.looseObject({
    14→  type: z.literal("tool_use"),
    15→  id: z.string(),
    16→  name: z.string(),
    17→  input: z.record(z.string(), z.unknown()),
    18→});
    19→
    20→// Getter defers evaluation to handle circular reference with ContentBlockSchema
    21→export const ToolResultBlockSchema = z.looseObject({
    22→  type: z.literal("tool_result"),
    23→  tool_use_id: z.string(),
    24→  get content(): z.ZodUnion<[z.ZodString, z.ZodArray<z.ZodTypeAny>]> {
    25→    return z.union([z.string(), z.array(ContentBlockSchema)]);
    26→  },
    27→});
    28→
    29→const Base64SourceSchema = z.object({
    30→  type: z.literal("base64"),
    31→  media_type: z.string(),
    32→  data: z.string(),
    33→});
    34→
    35→export const ImageBlockSchema = z.looseObject({
    36→  type: z.literal("image"),
    37→  source: Base64SourceSchema,
    38→});
    39→
    40→export const DocumentBlockSchema = z.looseObject({
    41→  type: z.literal("document"),
    42→  source: Base64SourceSchema,
    43→});
    44→
    45→export const KnownContentBlockSchema = z.discriminatedUnion("type", [
    46→  TextBlockSchema,
    47→  ThinkingBlockSchema,
    48→  ToolUseBlockSchema,
    49→  ToolResultBlockSchema,
    50→  ImageBlockSchema,
    51→  DocumentBlockSchema,
    52→]);
    53→
    54→export const UnknownContentBlockSchema = z.looseObject({ type: z.string() });
    55→
    56→export const ContentBlockSchema = z.union([
    57→  KnownContentBlockSchema,
    58→  UnknownContentBlockSchema,
    59→]);
    60→
    61→export type ContentBlock = z.infer<typeof ContentBlockSchema>;
    62→export type KnownContentBlock = z.infer<typeof KnownContentBlockSchema>;
    63→
    64→export const MessageContentSchema = z.union([
    65→  z.string(),
    66→  z.array(ContentBlockSchema),
    67→]);
    68→
    69→const BaseMessageSchema = z.looseObject({
    70→  role: z.string(),
    71→  content: MessageContentSchema.optional(),
    72→});
    73→
    74→// Transform strips id/usage fields (low retrieval value)
    75→export const UserMessageObjectSchema = z
    76→  .looseObject({
    77→    role: z.string(),
    78→    content: MessageContentSchema.optional(),
    79→    id: z.string().optional(),
    80→    usage: z.unknown().optional(),
    81→  })
    82→  .transform(({ id, usage, ...rest }) => rest);
    83→
    84→export const AssistantMessageObjectSchema = z
    85→  .looseObject({
    86→    role: z.string(),
    87→    content: MessageContentSchema.optional(),
    88→    model: z.string().optional(),
    89→    stop_reason: z.string().optional(),
    90→    id: z.string().optional(),
    91→    usage: z.unknown().optional(),
    92→  })
    93→  .transform(({ id, usage, ...rest }) => rest);
    94→
    95→export const SummaryEntrySchema = z.looseObject({
    96→  type: z.literal("summary"),
    97→  summary: z.string(),
    98→  leafUuid: z.string().optional(),
    99→});
   100→
   101→// Transform strips low-value fields (toolUseResult is redundant with message.content tool_result blocks)
   102→export const UserEntrySchema = z
   103→  .looseObject({
   104→    type: z.literal("user"),
   105→    uuid: z.string(),
   106→    parentUuid: z.string().nullable(),
   107→    timestamp: z.string(),
   108→    sessionId: z.string().optional(),
