   122→  .transform(
   123→    ({ toolUseResult, requestId, slug, userType, imagePasteIds, thinkingMetadata, todos, ...rest }) => rest,
   124→  );
   125→
   126→// Transform strips low-value fields: requestId, slug, userType
   127→export const AssistantEntrySchema = z
   128→  .looseObject({
   129→    type: z.literal("assistant"),
   130→    uuid: z.string(),
   131→    parentUuid: z.string().nullable(),
   132→    timestamp: z.string(),
   133→    sessionId: z.string().optional(),
   134→    message: AssistantMessageObjectSchema,
   135→    requestId: z.string().optional(),
   136→    slug: z.string().optional(),
   137→    userType: z.string().optional(),
   138→  })
   139→  .transform(({ requestId, slug, userType, ...rest }) => rest);
   140→
   141→export const SystemEntrySchema = z.looseObject({
   142→  type: z.literal("system"),
   143→  subtype: z.string().optional(),
   144→  uuid: z.string().optional(),
   145→  parentUuid: z.string().nullable().optional(),
   146→  timestamp: z.string().optional(),
   147→  content: z.string().optional(),
   148→  level: z.string().optional(),
   149→});
   150→
   151→export const FileHistorySnapshotSchema = z.looseObject({
   152→  type: z.literal("file-history-snapshot"),
   153→});
   154→
   155→export const QueueOperationSchema = z.looseObject({
   156→  type: z.literal("queue-operation"),
   157→});
   158→
   159→export const KnownEntrySchema = z.discriminatedUnion("type", [
   160→  SummaryEntrySchema,
   161→  UserEntrySchema,
   162→  AssistantEntrySchema,
   163→  SystemEntrySchema,
   164→  FileHistorySnapshotSchema,
   165→  QueueOperationSchema,
   166→]);
   167→
   168→export const UnknownEntrySchema = z.looseObject({ type: z.string() });
   169→
   170→export const EntrySchema = z.union([KnownEntrySchema, UnknownEntrySchema]);
   171→
   172→export type Entry = z.infer<typeof EntrySchema>;
   173→export type KnownEntry = z.infer<typeof KnownEntrySchema>;
   174→export type SummaryEntry = z.infer<typeof SummaryEntrySchema>;
   175→export type UserEntry = z.infer<typeof UserEntrySchema>;
   176→export type AssistantEntry = z.infer<typeof AssistantEntrySchema>;
   177→export type SystemEntry = z.infer<typeof SystemEntrySchema>;
   178→
   179→export function parseKnownEntry(data: unknown): KnownEntry | null {
   180→  const parsed = KnownEntrySchema.safeParse(data);
   181→  return parsed.success ? parsed.data : null;
   182→}
   183→
   184→export const HiveMindMetaSchema = z.object({
   185→  _type: z.literal("hive-mind-meta"),
   186→  version: z.string(),
   187→  sessionId: z.string(),
   188→  machineId: z.string(),
   189→  extractedAt: z.string(),
   190→  rawMtime: z.string(),
   191→  messageCount: z.number(),
   192→  summary: z.string().optional(),
   193→  rawPath: z.string(),
   194→  agentId: z.string().optional(),
   195→  parentSessionId: z.string().optional(),
   196→});
   197→
   198→export type HiveMindMeta = z.infer<typeof HiveMindMetaSchema>;
   199→
   200→// Content block types after base64 data is replaced with size info
   201→export interface TransformedImageBlock {
   202→  type: "image";
   203→  size: number;
   204→}
   205→
   206→export interface TransformedDocumentBlock {
   207→  type: "document";
   208→  media_type: string;
   209→  size: number;
   210→}
   211→
   212→export interface TransformedToolResultBlock {
   213→  type: "tool_result";
