     1→import { createReadStream } from "node:fs";
     2→import { mkdir, readFile, readdir, stat, writeFile } from "node:fs/promises";
     3→import { createInterface } from "node:readline";
     4→import { homedir } from "node:os";
     5→import { basename, dirname, join } from "node:path";
     6→import { getCheckoutId } from "./config";
     7→import { sanitizeDeep } from "./sanitize";
     8→import {
     9→  
    10→  HiveMindMetaSchema,
    11→  
    12→  parseKnownEntry
    13→} from "./schemas";
    14→import type {HiveMindMeta, KnownEntry} from "./schemas";
    15→
    16→const HIVE_MIND_VERSION = "0.1" as const;
    17→
    18→const INCLUDED_ENTRY_TYPES = ["user", "assistant", "summary", "system"] as const;
    19→
    20→export function* parseJsonl(content: string) {
    21→  for (const line of content.split("\n")) {
    22→    const trimmed = line.trim();
    23→    if (!trimmed) continue;
    24→    try {
    25→      yield JSON.parse(trimmed) as unknown;
    26→    } catch (error) {
    27→      if (process.env.DEBUG) {
    28→        console.warn("Skipping malformed JSONL line:", error);
    29→      }
    30→    }
    31→  }
    32→}
    33→
    34→function transformEntry(rawEntry: unknown) {
    35→  const entry = parseKnownEntry(rawEntry);
    36→  if (!entry) {
    37→    if (process.env.DEBUG) {
    38→      console.warn("Skipping unknown entry type");
    39→    }
    40→    return null;
    41→  }
    42→
    43→  if (INCLUDED_ENTRY_TYPES.includes(entry.type as (typeof INCLUDED_ENTRY_TYPES)[number])) {
    44→    return entry;
    45→  }
    46→
    47→  return null;
    48→}
    49→
    50→type ExtractedEntry = Exclude<ReturnType<typeof transformEntry>, null>;
    51→
    52→/** Returns the summary where leafUuid exists in the same session. */
    53→function findValidSummary(entries: Array<ExtractedEntry>) {
    54→  const uuids = new Set<string>();
    55→  const summaries: Array<{ summary: string; leafUuid?: string }> = [];
    56→
    57→  for (const entry of entries) {
    58→    if ("uuid" in entry && typeof entry.uuid === "string") {
    59→      uuids.add(entry.uuid);
    60→    }
    61→    if (entry.type === "summary") {
    62→      summaries.push({
    63→        summary: entry.summary,
    64→        leafUuid: entry.leafUuid,
    65→      });
    66→    }
    67→  }
    68→
    69→  // Find a summary whose leafUuid exists in this file (not cross-contaminated)
    70→  for (const s of summaries) {
    71→    if (s.leafUuid && uuids.has(s.leafUuid)) {
    72→      return s.summary;
    73→    }
    74→  }
    75→
    76→  // Fallback: return the last summary if no valid one found
    77→  return summaries.at(-1)?.summary;
    78→}
    79→
    80→interface ExtractSessionOptions {
    81→  rawPath: string;
    82→  outputPath: string;
    83→  agentId?: string;
    84→}
    85→
    86→export async function extractSession(options: ExtractSessionOptions) {
    87→  const { rawPath, outputPath, agentId } = options;
    88→
    89→  // outputPath is <cwd>/.claude/hive-mind/sessions/<id>.jsonl
    90→  // hiveMindDir is <cwd>/.claude/hive-mind
    91→  const hiveMindDir = dirname(dirname(outputPath));
    92→
    93→  const [content, rawStat, checkoutId] = await Promise.all([
    94→    readFile(rawPath, "utf-8"),
    95→    stat(rawPath),
    96→    getCheckoutId(hiveMindDir),
    97→  ]);
    98→
    99→  const entries: Array<ExtractedEntry> = [];
   100→
   101→  for (const rawEntry of parseJsonl(content)) {
   102→    const transformed = transformEntry(rawEntry);
   103→    if (transformed) {
   104→      entries.push(transformed);
   105→    }
   106→  }
   107→
   108→  // Skip sessions with no assistant messages
   109→  const hasAssistantMessage = entries.some((e) => e.type === "assistant");
   110→  if (!hasAssistantMessage) {
   111→    return null;
   112→  }
   113→
   114→  // For agent sessions, extract parentSessionId from first entry with sessionId
   115→  const parentSessionId = agentId
   116→    ? entries.find((e): e is ExtractedEntry & { sessionId: string } => "sessionId" in e && typeof e.sessionId === "string")?.sessionId
   117→    : undefined;
   118→
   119→  const summary = findValidSummary(entries);
   120→  const sessionId = basename(rawPath, ".jsonl");
   121→
   122→  const meta: HiveMindMeta = {
   123→    _type: "hive-mind-meta",
   124→    version: HIVE_MIND_VERSION,
   125→    sessionId,
   126→    checkoutId,
   127→    extractedAt: new Date().toISOString(),
   128→    rawMtime: rawStat.mtime.toISOString(),
   129→    messageCount: entries.length,
   130→    summary,
   131→    rawPath,
   132→    ...(agentId && { agentId }),
   133→    ...(parentSessionId && { parentSessionId }),
   134→  };
   135→
   136→  const sanitizedEntries = entries.map((e) => sanitizeDeep(e));
   137→
   138→  // Write output
   139→  await mkdir(dirname(outputPath), { recursive: true });
   140→
   141→  const lines = [
   142→    JSON.stringify(meta),
   143→    ...sanitizedEntries.map((e) => JSON.stringify(e)),
   144→  ];
   145→
   146→  await writeFile(outputPath, `${lines.join("\n")}\n`);
   147→
   148→  return { messageCount: entries.length, summary };
   149→}
   150→
   151→async function readFirstLine(filePath: string): Promise<string | null> {
   152→  const stream = createReadStream(filePath, { encoding: "utf-8" });
   153→  const rl = createInterface({ input: stream, crlfDelay: Infinity });
   154→
   155→  try {
   156→    for await (const line of rl) {
   157→      stream.destroy();
   158→      return line;
   159→    }
   160→    return null;
   161→  } finally {
   162→    rl.close();
   163→  }
   164→}
   165→
   166→export async function readExtractedMeta(
   167→  extractedPath: string,
   168→): Promise<HiveMindMeta | null> {
   169→  try {
   170→    const firstLine = await readFirstLine(extractedPath);
   171→    if (!firstLine) return null;
   172→
   173→    const parsed = HiveMindMetaSchema.safeParse(JSON.parse(firstLine));
   174→    return parsed.success ? parsed.data : null;
   175→  } catch {
   176→    return null;
   177→  }
   178→}
   179→
   180→export function getProjectsDir(cwd: string): string {
   181→  // Encode cwd: replace / with -
   182→  const encoded = cwd.replace(/\//g, "-");
   183→  return join(homedir(), ".claude", "projects", encoded);
   184→}
   185→
   186→export function getHiveMindSessionsDir(projectCwd: string): string {
   187→  return join(projectCwd, ".claude", "hive-mind", "sessions");
   188→}
   189→
   190→/** Returns both regular sessions and agent sessions. */
   191→export async function findRawSessions(rawDir: string) {
   192→  try {
   193→    const files = await readdir(rawDir);
   194→    const sessions: Array<{ path: string; agentId?: string }> = [];
   195→
   196→    for (const f of files) {
   197→      if (!f.endsWith(".jsonl")) continue;
   198→
   199→      if (f.startsWith("agent-")) {
   200→        // Agent session: agent-<agentId>.jsonl
   201→        const agentId = f.replace("agent-", "").replace(".jsonl", "");
   202→        sessions.push({ path: join(rawDir, f), agentId });
   203→      } else {
   204→        // Regular session
   205→        sessions.push({ path: join(rawDir, f) });
   206→      }
   207→    }
   208→
   209→    return sessions;
   210→  } catch {
   211→    return [];
   212→  }
   213→}
   214→
   215→export async function needsExtraction(
   216→  rawPath: string,
   217→  extractedPath: string,
   218→): Promise<boolean> {
   219→  try {
   220→    const rawStat = await stat(rawPath);
   221→    const meta = await readExtractedMeta(extractedPath);
   222→
   223→    if (!meta) return true;
   224→
   225→    // Compare mtimes
   226→    const rawMtime = rawStat.mtime.toISOString();
   227→    return rawMtime !== meta.rawMtime;
   228→  } catch {
   229→    return true;
   230→  }
   231→}
   232→
   233→export async function extractAllSessions(cwd: string, transcriptPath?: string) {
   234→  // Determine raw sessions directory
   235→  const rawDir = transcriptPath ? dirname(transcriptPath) : getProjectsDir(cwd);
   236→  const extractedDir = getHiveMindSessionsDir(cwd);
   237→
   238→  const rawSessions = await findRawSessions(rawDir);
   239→  let extracted = 0;
   240→
   241→  for (const session of rawSessions) {
   242→    const { path: rawPath, agentId } = session;
   243→
   244→    // Use same filename as original (agent-<id>.jsonl or <sessionId>.jsonl)
   245→    const extractedPath = join(extractedDir, basename(rawPath));
   246→
   247→    if (await needsExtraction(rawPath, extractedPath)) {
   248→      try {
   249→        const result = await extractSession({ rawPath, outputPath: extractedPath, agentId });
   250→        if (result) {
   251→          extracted++;
   252→        }
   253→      } catch (error) {
   254→        // Log but continue with other sessions
   255→        const id = basename(rawPath, ".jsonl");
   256→        console.error(`Failed to extract ${id}:`, error);
   257→      }
   258→    }
   259→  }
   260→
   261→  return extracted;
   262→}
