   211→  const lines = [
   212→    JSON.stringify(meta),
   213→    ...sanitizedEntries.map((e) => JSON.stringify(e)),
   214→  ];
   215→
   216→  await writeFile(outputPath, `${lines.join("\n")}\n`);
   217→
   218→  return { messageCount: entries.length, summary };
