     1→/**
     2→ * Index command - list extracted sessions.
     3→ * Agent sessions excluded - explore via Task tool calls in parent sessions.
     4→ */
     5→
     6→import { readdir } from "node:fs/promises";
     7→import { join } from "node:path";
     8→import { getHiveMindSessionsDir, readExtractedSession } from "../lib/extraction";
     9→import { printError } from "../lib/output";
    10→import type { ContentBlock, HiveMindMeta, KnownEntry } from "../lib/schemas";
    11→
    12→interface SessionInfo {
    13→  meta: HiveMindMeta;
    14→  entries: Array<KnownEntry>;
    15→}
    16→
    17→export async function index(): Promise<void> {
    18→  const cwd = process.cwd();
    19→  const sessionsDir = getHiveMindSessionsDir(cwd);
    20→
    21→  let files: Array<string>;
    22→  try {
    23→    files = await readdir(sessionsDir);
    24→  } catch {
    25→    printError(`No sessions found. Run 'extract' first.`);
    26→    return;
    27→  }
    28→
    29→  const jsonlFiles = files.filter((f) => f.endsWith(".jsonl"));
    30→  if (jsonlFiles.length === 0) {
    31→    printError(`No sessions found in ${sessionsDir}`);
    32→    return;
    33→  }
    34→
    35→  const sessions: Array<SessionInfo> = [];
    36→  for (const file of jsonlFiles) {
    37→    const path = join(sessionsDir, file);
    38→    const session = await readExtractedSession(path);
    39→    if (session && !session.meta.agentId) {
    40→      sessions.push(session);
    41→    }
    42→  }
    43→
    44→  sessions.sort((a, b) => b.meta.rawMtime.localeCompare(a.meta.rawMtime));
    45→
    46→  console.log("ID\tDATETIME\tMSGS\tSUMMARY\tCOMMITS");
    47→  for (const session of sessions) {
    48→    console.log(formatSessionLine(session));
    49→  }
    50→}
    51→
    52→function formatSessionLine(session: SessionInfo): string {
    53→  const { meta, entries } = session;
    54→  const id = meta.sessionId.slice(0, 16);
    55→  const datetime = meta.rawMtime.slice(0, 16);
    56→  const count = String(meta.messageCount);
    57→  const summary = findSummary(entries) || findFirstUserPrompt(entries) || "";
    58→
    59→  const commits = findGitCommits(entries).filter((c) => c.success);
    60→  const commitList = commits.map((c) =>
    61→    c.message.length > 100 ? `${c.message.slice(0, 97)}...` : c.message
    62→  ).join("; ");
    63→
    64→  return `${id}\t${datetime}\t${count}\t${summary}\t${commitList}`;
    65→}
    66→
    67→function findSummary(entries: Array<KnownEntry>): string | undefined {
    68→  const uuids = new Set<string>();
    69→  const summaries: Array<{ summary: string; leafUuid?: string }> = [];
    70→
    71→  for (const entry of entries) {
    72→    if ("uuid" in entry && typeof entry.uuid === "string") {
    73→      uuids.add(entry.uuid);
    74→    }
    75→    if (entry.type === "summary") {
    76→      summaries.push({ summary: entry.summary, leafUuid: entry.leafUuid });
    77→    }
    78→  }
    79→
    80→  for (const s of summaries) {
    81→    if (s.leafUuid && uuids.has(s.leafUuid)) {
    82→      return s.summary;
    83→    }
    84→  }
    85→
    86→  return summaries.at(-1)?.summary;
    87→}
    88→
    89→function findFirstUserPrompt(entries: Array<KnownEntry>): string | undefined {
    90→  for (const entry of entries) {
    91→    if (entry.type !== "user") continue;
    92→    const content = entry.message.content;
    93→    if (!content) continue;
    94→
    95→    let text: string | undefined;
    96→    if (typeof content === "string") {
    97→      text = content;
    98→    } else if (Array.isArray(content)) {
    99→      for (const block of content) {
   100→        if (block.type === "text" && "text" in block && typeof block.text === "string") {
   101→          text = block.text;
   102→          break;
   103→        }
   104→      }
   105→    }
   106→
   107→    if (text) {
   108→      const firstLine = text.split("\n")[0].trim();
   109→      if (firstLine) {
   110→        return firstLine.length > 100 ? `${firstLine.slice(0, 97)}...` : firstLine;
   111→      }
   112→    }
   113→  }
   114→  return undefined;
   115→}
   116→
   117→interface GitCommit {
   118→  message: string;
   119→  success: boolean;
   120→}
   121→
   122→function findGitCommits(entries: Array<KnownEntry>): Array<GitCommit> {
   123→  const commits: Array<GitCommit> = [];
   124→  const pendingCommits = new Map<string, string>();
   125→
   126→  for (const entry of entries) {
   127→    if (entry.type === "assistant") {
   128→      const content = entry.message.content;
   129→      if (!Array.isArray(content)) continue;
   130→
   131→      for (const block of content) {
   132→        if (block.type === "tool_use" && "name" in block && block.name === "Bash") {
   133→          const input = block.input;
   134→          const command = input.command;
   135→          if (typeof command === "string" && command.includes("git commit")) {
   136→            const message = extractCommitMessage(command);
   137→            if (message && "id" in block && typeof block.id === "string") {
   138→              pendingCommits.set(block.id, message);
   139→            }
   140→          }
   141→        }
   142→      }
   143→    } else if (entry.type === "user") {
   144→      const content = entry.message.content;
   145→      if (!Array.isArray(content)) continue;
   146→
   147→      for (const block of content) {
   148→        if (block.type === "tool_result" && "tool_use_id" in block) {
   149→          const toolUseId = block.tool_use_id;
   150→          const message = pendingCommits.get(toolUseId);
   151→          if (message) {
   152→            const resultContent = getToolResultText(block.content as string | Array<ContentBlock> | undefined);
   153→            const success = resultContent.includes("[") && !resultContent.includes("error");
   154→            commits.push({ message, success });
   155→            pendingCommits.delete(toolUseId);
   156→          }
   157→        }
   158→      }
   159→    }
   160→  }
   161→
   162→  for (const message of pendingCommits.values()) {
   163→    commits.push({ message, success: true });
   164→  }
   165→
   166→  return commits;
   167→}
   168→
   169→function extractCommitMessage(command: string): string | undefined {
   170→  // Heredoc: -m "$(cat <<'EOF'\nmessage\nEOF\n)"
   171→  const heredocMatch = command.match(/<<['"]?EOF['"]?\s*\n([\s\S]*?)\n\s*EOF/);
   172→  if (heredocMatch) {
   173→    const firstLine = heredocMatch[1].trim().split("\n")[0].trim();
   174→    if (firstLine) return firstLine;
   175→  }
   176→
   177→  // -m "message" (not heredoc)
   178→  const mFlagMatch = command.match(/git commit[^"']*-m\s*["'](?!\$\()([^"']+)["']/);
   179→  if (mFlagMatch) return mFlagMatch[1].trim();
   180→
   181→  // Simple -m message (no quotes)
   182→  const simpleMatch = command.match(/git commit[^-]*-m\s+(\S+)/);
   183→  if (simpleMatch && !simpleMatch[1].startsWith('"') && !simpleMatch[1].startsWith("'")) {
   184→    return simpleMatch[1];
   185→  }
   186→
   187→  return undefined;
   188→}
   189→
   190→function getToolResultText(content: string | Array<ContentBlock> | undefined): string {
   191→  if (!content) return "";
   192→  if (typeof content === "string") return content;
   193→
   194→  const parts: Array<string> = [];
   195→  for (const block of content) {
   196→    if (block.type === "text" && "text" in block) {
   197→      parts.push(block.text);
   198→    }
   199→  }
   200→  return parts.join("\n");
   201→}
