     1→import { mkdir } from "node:fs/promises";
     2→import { z } from "zod";
     3→import { AUTH_DIR, [REDACTED:generic-api-key]} from "./config";
     4→
     5→const WORKOS_API_URL = "https://api.workos.com/user_management";
     6→
     7→const AuthUserSchema = z.object({
     8→  id: z.string(),
     9→  email: z.string(),
    10→  first_name: z.string().optional(),
    11→  last_name: z.string().optional(),
    12→});
    13→
    14→export const AuthDataSchema = z.object({
    15→  access_token: z.string(),
    16→  refresh_token: z.string(),
    17→  user: AuthUserSchema,
    18→});
    19→
    20→export type AuthUser = z.infer<typeof AuthUserSchema>;
    21→export type AuthData = z.infer<typeof AuthDataSchema>;
    22→
    23→function decodeJwtPayload(token: string): Record<string, unknown> | null {
    24→  try {
    25→    const parts = token.split(".");
    26→    if (parts.length !== 3) return null;
    27→
    28→    let payload = parts[1];
    29→    const padding = 4 - (payload.length % 4);
    30→    if (padding < 4) {
    31→      payload += "=".repeat(padding);
    32→    }
    33→
    34→    return JSON.parse(atob(payload));
    35→  } catch {
    36→    return null;
    37→  }
    38→}
    39→
    40→function isTokenExpired(token: string): boolean {
    41→  const payload = decodeJwtPayload(token);
    42→  if (!payload || typeof payload.exp !== "number") return true;
    43→  return payload.exp <= Math.floor(Date.now() / 1000);
    44→}
    45→
    46→export async function loadAuthData(): Promise<AuthData | null> {
    47→  try {
    48→    const file = Bun.file(AUTH_FILE);
    49→    if (!(await file.exists())) return null;
    50→    const data = await file.json();
    51→    const parsed = AuthDataSchema.safeParse(data);
    52→    return parsed.success ? parsed.data : null;
    53→  } catch {
    54→    return null;
    55→  }
    56→}
    57→
    58→export async function saveAuthData(data: AuthData): Promise<void> {
    59→  await mkdir(AUTH_DIR, { recursive: true });
    60→  await Bun.write(AUTH_FILE, JSON.stringify(data, null, 2), { mode: 0o600 });
    61→}
    62→
    63→export async function refreshToken(
    64→  refreshTokenValue: string,
    65→): Promise<AuthData | null> {
    66→  try {
    67→    const response = await fetch(`${WORKOS_API_URL}/authenticate`, {
    68→      method: "POST",
    69→      headers: { "Content-Type": "application/x-www-form-urlencoded" },
    70→      body: new URLSearchParams({
    71→        grant_type: "refresh_token",
    72→        refresh_token: refreshTokenValue,
    73→        client_id: WORKOS_CLIENT_ID,
    74→      }),
    75→    });
    76→
    77→    const data = await response.json();
    78→    const parsed = AuthDataSchema.safeParse(data);
    79→    return parsed.success ? parsed.data : null;
    80→  } catch {
    81→    return null;
    82→  }
    83→}
    84→
    85→export interface AuthStatus {
    86→  authenticated: boolean;
    87→  user?: AuthUser;
    88→  needsLogin: boolean;
    89→}
    90→
    91→export async function checkAuthStatus(
    92→  attemptRefresh = true,
    93→): Promise<AuthStatus> {
    94→  const authData = await loadAuthData();
    95→
    96→  if (!authData?.access_token) {
    97→    return { authenticated: false, needsLogin: true };
    98→  }
    99→
   100→  if (isTokenExpired(authData.access_token)) {
   101→    if (!attemptRefresh || !authData.refresh_token) {
   102→      return { authenticated: false, needsLogin: true };
   103→    }
   104→
   105→    const newAuthData = await refreshToken(authData.refresh_token);
   106→    if (!newAuthData) {
   107→      return { authenticated: false, needsLogin: true };
   108→    }
   109→
   110→    await saveAuthData(newAuthData);
   111→    return { authenticated: true, user: newAuthData.user, needsLogin: false };
   112→  }
   113→
   114→  return { authenticated: true, user: authData.user, needsLogin: false };
   115→}
   116→
   117→export function getUserDisplayName(user: AuthUser): string {
   118→  return user.first_name || user.email;
   119→}
