     1→import { randomUUID } from "node:crypto";
     2→import { mkdir, readFile, writeFile } from "node:fs/promises";
     3→import { homedir } from "node:os";
     4→import { join } from "node:path";
     5→
     6→export const WORKOS_CLIENT_ID =
     7→  process.env.HIVE_MIND_CLIENT_ID ?? "client_01KE10CYZ10VVZPJVRQBJESK1A";
     8→
     9→export const AUTH_DIR = join(homedir(), ".claude", "hive-mind");
    10→export const AUTH_FILE = join(AUTH_DIR, "auth.json");
    11→export const MACHINE_ID_FILE = join(AUTH_DIR, "machine-id");
    12→
    13→/**
    14→ * Get or create a persistent machine ID for anonymous tracking.
    15→ * Generated on first call, persisted in ~/.claude/hive-mind/machine-id
    16→ */
    17→export async function getMachineId() {
    18→  try {
    19→    const id = await readFile(MACHINE_ID_FILE, "utf-8");
    20→    return id.trim();
    21→  } catch {
    22→    const id = randomUUID();
    23→    await mkdir(AUTH_DIR, { recursive: true });
    24→    await writeFile(MACHINE_ID_FILE, id);
    25→    return id;
    26→  }
    27→}
    28→
    29→export function getShellConfig(): { file: string; sourceCmd: string } {
    30→  const shell = process.env.SHELL ?? "/bin/bash";
    31→  if (shell.includes("zsh")) {
    32→    return { file: "~/.zshrc", sourceCmd: "source ~/.zshrc" };
    33→  }
    34→  if (shell.includes("bash")) {
    35→    return { file: "~/.bashrc", sourceCmd: "source ~/.bashrc" };
    36→  }
    37→  if (shell.includes("fish")) {
    38→    return {
    39→      file: "~/.config/fish/config.fish",
    40→      sourceCmd: "source ~/.config/fish/config.fish",
    41→    };
    42→  }
    43→  return { file: "~/.profile", sourceCmd: "source ~/.profile" };
    44→}
