     1→#!/usr/bin/env bun
     2→
     3→import { extract } from "./commands/extract";
     4→import { login } from "./commands/login";
     5→import { sessionStart } from "./commands/session-start";
     6→import { printError } from "./lib/output";
     7→
     8→const COMMANDS = {
     9→  extract: { description: "Extract sessions for retrieval", handler: extract },
    10→  login: { description: "Authenticate with hive-mind", handler: login },
    11→  "session-start": { description: "SessionStart hook", handler: sessionStart },
    12→} as const;
    13→
    14→type CommandName = keyof typeof COMMANDS;
    15→
    16→function printUsage(): void {
    17→  console.log("Usage: hive-mind <command>\n");
    18→  console.log("Commands:");
    19→  for (const [name, { description }] of Object.entries(COMMANDS)) {
    20→    console.log(`  ${name.padEnd(15)} ${description}`);
    21→  }
    22→}
    23→
    24→async function main(): Promise<void> {
    25→  const command = process.argv[2];
    26→
    27→  if (
    28→    !command ||
    29→    command === "help" ||
    30→    command === "--help" ||
    31→    command === "-h"
    32→  ) {
    33→    printUsage();
    34→    if (!command) process.exit(1);
    35→    return;
    36→  }
    37→
    38→  const cmd = COMMANDS[command as CommandName];
    39→  if (!cmd) {
    40→    printError(`Unknown command: ${command}`);
    41→    console.log("");
    42→    printUsage();
    43→    process.exit(1);
    44→  }
    45→
    46→  try {
    47→    await cmd.handler();
    48→  } catch (error) {
    49→    printError(error instanceof Error ? error.message : String(error));
    50→    process.exit(1);
    51→  }
    52→}
    53→
    54→main();
    55→

