     1→import { basename, join } from "node:path";
     2→import {
     3→  extractSession,
     4→  findRawSessions,
     5→  getHiveMindSessionsDir,
     6→  getProjectsDir,
     7→  needsExtraction,
     8→} from "../lib/extraction";
     9→import { printError, printInfo, printSuccess } from "../lib/output";
    10→
    11→export async function extract(): Promise<void> {
    12→  const cwd = process.cwd();
    13→  const rawDir = getProjectsDir(cwd);
    14→  const extractedDir = getHiveMindSessionsDir(cwd);
    15→
    16→  printInfo(`Scanning for sessions in ${rawDir}...`);
    17→
    18→  const sessions = await findRawSessions(rawDir);
    19→  if (sessions.length === 0) {
    20→    printInfo("No sessions found to extract.");
    21→    return;
    22→  }
    23→
    24→  printInfo(
    25→    `Found ${sessions.length} sessions. Checking which need extraction...`,
    26→  );
    27→
    28→  // Find sessions that need extraction
    29→  const toExtract: typeof sessions = [];
    30→  for (const session of sessions) {
    31→    const { path: rawPath } = session;
    32→    // Use same filename as original (agent-<id>.jsonl or <sessionId>.jsonl)
    33→    const extractedPath = join(extractedDir, basename(rawPath));
    34→
    35→    if (await needsExtraction(rawPath, extractedPath)) {
    36→      toExtract.push(session);
    37→    }
    38→  }
    39→
    40→  if (toExtract.length === 0) {
    41→    printSuccess("All sessions already extracted.");
    42→    return;
    43→  }
    44→
    45→  printInfo(`Processing ${toExtract.length} sessions...`);
    46→  console.log("");
    47→
    48→  let extracted = 0;
    49→  let skipped = 0;
    50→  let failed = 0;
    51→
    52→  for (let i = 0; i < toExtract.length; i++) {
    53→    const session = toExtract[i];
    54→    const { path: rawPath, agentId } = session;
    55→    const id = basename(rawPath, ".jsonl");
    56→
    57→    // Use same filename as original
    58→    const extractedPath = join(extractedDir, basename(rawPath));
    59→
    60→    const progress = `[${i + 1}/${toExtract.length}]`;
    61→    process.stdout.write(`\r  ${progress} Processing ${id.slice(0, 8)}...`);
    62→
    63→    try {
    64→      const result = await extractSession({ rawPath, outputPath: extractedPath, agentId });
    65→      if (result) {
    66→        extracted++;
    67→      } else {
    68→        skipped++;
    69→      }
    70→    } catch (error) {
    71→      failed++;
    72→      console.log("");
    73→      printError(`Failed to extract ${id}: ${error}`);
    74→    }
    75→  }
    76→
    77→  console.log("");
    78→  console.log("");
    79→
    80→  const parts = [`Extracted ${extracted} sessions`];
    81→  if (skipped > 0) parts.push(`${skipped} skipped (empty)`);
    82→  if (failed > 0) parts.push(`${failed} failed`);
    83→  printSuccess(parts.join(", ") + ".");
    84→
    85→  console.log(`Output: ${extractedDir}`);
    86→}
    87→

