    38→async function confirm(message: string): Promise<boolean> {
    39→  const rl = createInterface({ input: process.stdin, output: process.stdout });
    40→  return new Promise((resolve) => {
    41→    rl.question(`${message} [y/N] `, (answer) => {
    42→      rl.close();
    43→      resolve(answer.toLowerCase() === "y");
    44→    });
    45→  });
    46→}
    47→
    48→async function openBrowser(url: string): Promise<boolean> {
    49→  try {
    50→    if (process.platform === "darwin") {
    51→      await Bun.spawn(["open", url]).exited;
    52→      return true;
    53→    } else if (process.platform === "linux") {
    54→      try {
    55→        await Bun.spawn(["xdg-open", url]).exited;
    56→        return true;
    57→      } catch {
    58→        try {
    59→          await Bun.spawn(["wslview", url]).exited;
    60→          return true;
    61→        } catch {
    62→          return false;
    63→        }
    64→      }
    65→    }
    66→    return false;
    67→  } catch {
    68→    return false;
    69→  }
    70→}
    71→
    72→function sleep(ms: number): Promise<void> {
    73→  return new Promise((resolve) => setTimeout(resolve, ms));
    74→}
    75→
    76→async function checkExistingAuth(): Promise<boolean> {
    77→  const status = await checkAuthStatus(false);
    78→
    79→  if (status.authenticated && status.user) {
    80→    printWarning(msg.alreadyLoggedIn);
    81→    console.log("");
    82→    return await confirm(msg.confirmRelogin);
    83→  }
    84→
    85→  return true;
    86→}
    87→
    88→async function tryRefresh(): Promise<boolean> {
    89→  const authData = await loadAuthData();
    90→  if (!authData?.refresh_token) return false;
    91→
    92→  printInfo(msg.refreshing);
    93→
    94→  const newAuthData = await refreshToken(authData.refresh_token);
    95→  if (newAuthData) {
    96→    await saveAuthData(newAuthData);
    97→    printSuccess(msg.refreshSuccess);
    98→    return true;
    99→  }
   100→
   101→  return false;
   102→}
   103→
   104→async function deviceAuthFlow(): Promise<void> {
   105→  printInfo(msg.starting);
   106→  console.log("");
   107→
   108→  const response = await fetch(`${WORKOS_API_URL}/authorize/device`, {
   109→    method: "POST",
   110→    headers: { "Content-Type": "application/x-www-form-urlencoded" },
   111→    body: new URLSearchParams({ client_id: WORKOS_CLIENT_ID }),
   112→  });
   113→
   114→  const data = await response.json();
   115→
   116→  // Check for error response
   117→  const errorResult = ErrorResponseSchema.safeParse(data);
   118→  if (errorResult.success && errorResult.data.error) {
   119→    printError(msg.startFailed(errorResult.data.error));
   120→    if (errorResult.data.error_description) {
   121→      console.log(errorResult.data.error_description);
   122→    }
   123→    process.exit(1);
   124→  }
   125→
   126→  // Validate device auth response
   127→  const deviceAuthResult = DeviceAuthResponseSchema.safeParse(data);
   128→  if (!deviceAuthResult.success) {
   129→    printError("Unexpected response from authentication server");
   130→    process.exit(1);
   131→  }
   132→
   133→  const [REDACTED:generic-api-key]
   134→
   135→  console.log("\u2501".repeat(65));
   136→  console.log("");
   137→  console.log(`  ${msg.visitUrl}`);
   138→  console.log("");
   139→  console.log(`    ${deviceAuth.verification_uri}`);
   140→  console.log("");
   141→  console.log(`  ${msg.confirmCode}`);
   142→  console.log("");
   143→  console.log(`    ${colors.green(deviceAuth.user_code)}`);
   144→  console.log("");
   145→  console.log("\u2501".repeat(65));
   146→  console.log("");
   147→
   148→  if (await openBrowser(deviceAuth.verification_uri_complete)) {
   149→    printInfo(msg.browserOpened);
   150→  } else {
   151→    printInfo(msg.openManually);
   152→  }
   153→  console.log("");
   154→  printInfo(msg.waiting(deviceAuth.expires_in));
   155→
   156→  let interval = deviceAuth.interval * 1000;
   157→  const startTime = Date.now();
   158→  const expiresAt = startTime + deviceAuth.expires_in * 1000;
   159→
   160→  while (Date.now() < expiresAt) {
   161→    await sleep(interval);
   162→
   163→    const elapsed = Math.floor((Date.now() - startTime) / 1000);
   164→
   165→    const tokenResponse = await fetch(`${WORKOS_API_URL}/authenticate`, {
   166→      method: "POST",
   167→      headers: { "Content-Type": "application/x-www-form-urlencoded" },
   168→      body: new URLSearchParams({
   169→        grant_type: "urn:ietf:params:oauth:grant-type:device_code",
   170→        device_code: deviceAuth.device_code,
   171→        client_id: WORKOS_CLIENT_ID,
   172→      }),
   173→    });
   174→
   175→    const tokenData = await tokenResponse.json();
   176→
   177→    // Try to parse as successful auth response
   178→    const authResult = AuthDataSchema.safeParse(tokenData);
   179→    if (authResult.success) {
   180→      await saveAuthData(authResult.data);
   181→
   182→      console.log("");
   183→      printSuccess(msg.success);
   184→      console.log("");
   185→
   186→      const displayName = getUserDisplayName(authResult.data.user);
   187→      if (authResult.data.user.first_name) {
   188→        console.log(msg.welcomeNamed(displayName, authResult.data.user.email));
   189→      } else {
   190→        console.log(msg.welcomeEmail(authResult.data.user.email));
   191→      }
   192→      console.log("");
   193→      console.log(msg.contributing);
   194→      console.log(msg.reviewPeriod);
   195→
   196→      return;
