   199→    // Check for known error states
   200→    const errorData = tokenData as {
   201→      error?: string;
   202→      error_description?: string;
   203→    };
   204→
   205→    if (errorData.error === "authorization_pending") {
   206→      process.stdout.write(`\r  ${msg.waitingProgress(elapsed)}`);
   207→      continue;
   208→    }
   209→
   210→    if (errorData.error === "slow_down") {
   211→      interval += 1000;
   212→      continue;
   213→    }
   214→
   215→    console.log("");
   216→    printError(msg.authFailed(errorData.error || "unknown error"));
   217→    if (errorData.error_description) console.log(errorData.error_description);
   218→    process.exit(1);
   219→  }
   220→
   221→  printError(msg.timeout);
