     7→} from "../lib/messages";
     8→import { hookOutput } from "../lib/output";
     9→
    10→export async function sessionStart(): Promise<void> {
    11→  const messages: string[] = [];
    12→
    13→  // 1. Check auth status
    14→  const status = await checkAuthStatus(true);
    15→
    16→  if (status.needsLogin) {
    17→    messages.push(notLoggedInMessage());
    18→  } else if (status.user) {
    19→    messages.push(loggedInMessage(getUserDisplayName(status.user)));
    20→  }
    21→
    22→  // 2. Extract all sessions that need extraction
    23→  const cwd = process.env.CWD || process.cwd();
    24→  const transcriptPath = process.env.TRANSCRIPT_PATH;
    25→
    26→  try {
    27→    const extracted = await extractAllSessions(cwd, transcriptPath);
    28→    if (extracted > 0) {
    29→      messages.push(extractedMessage(extracted));
    30→    }
    31→  } catch (error) {
    32→    // Show error to user via systemMessage (Claude only sees "hook success")
    33→    const errorMsg = error instanceof Error ? error.message : String(error);
    34→    messages.push(`Extraction failed: ${errorMsg}`);
    35→  }
    36→
    37→  // Output all messages together
    38→  if (messages.length > 0) {
    39→    hookOutput(messages.join("\n"));
    40→  }
    41→}
    42→

