     1→import { createReadStream } from "node:fs";
     2→import { mkdir, readdir, readFile, stat, writeFile } from "node:fs/promises";
     3→import { createInterface } from "node:readline";
     4→import { homedir } from "node:os";
     5→import { basename, dirname, join } from "node:path";
     6→import { getMachineId } from "./config";
     7→import { sanitizeDeep } from "./sanitize";
     8→import {
     9→  type HiveMindMeta,
    10→  HiveMindMetaSchema,
    11→  type KnownEntry,
    12→  parseKnownEntry,
    13→} from "./schemas";
    14→
    15→const HIVE_MIND_VERSION = "0.1" as const;
    16→
    17→const INCLUDED_ENTRY_TYPES = ["user", "assistant", "summary", "system"] as const;
    18→
    19→export function* parseJsonl(content: string) {
    20→  for (const line of content.split("\n")) {
    21→    const trimmed = line.trim();
    22→    if (!trimmed) continue;
    23→    try {
    24→      yield JSON.parse(trimmed) as unknown;
    25→    } catch (error) {
    26→      if (process.env.DEBUG) {
    27→        console.warn("Skipping malformed JSONL line:", error);
    28→      }
    29→    }
    30→  }
    31→}
    32→
    33→function transformEntry(rawEntry: unknown) {
    34→  const entry = parseKnownEntry(rawEntry);
    35→  if (!entry) {
    36→    if (process.env.DEBUG) {
    37→      console.warn("Skipping unknown entry type");
    38→    }
    39→    return null;
    40→  }
    41→
    42→  if (INCLUDED_ENTRY_TYPES.includes(entry.type as (typeof INCLUDED_ENTRY_TYPES)[number])) {
    43→    return entry;
    44→  }
    45→
    46→  return null;
    47→}
    48→
    49→type ExtractedEntry = Exclude<ReturnType<typeof transformEntry>, null>;
    50→
    51→/** Returns the summary where leafUuid exists in the same session. */
    52→function findValidSummary(entries: ExtractedEntry[]) {
    53→  const uuids = new Set<string>();
    54→  const summaries: Array<{ summary: string; leafUuid?: string }> = [];
    55→
    56→  for (const entry of entries) {
    57→    if ("uuid" in entry && typeof entry.uuid === "string") {
    58→      uuids.add(entry.uuid);
    59→    }
    60→    if (entry.type === "summary") {
    61→      summaries.push({
    62→        summary: entry.summary,
    63→        leafUuid: entry.leafUuid,
    64→      });
    65→    }
    66→  }
    67→
    68→  // Find a summary whose leafUuid exists in this file (not cross-contaminated)
    69→  for (const s of summaries) {
    70→    if (s.leafUuid && uuids.has(s.leafUuid)) {
    71→      return s.summary;
    72→    }
    73→  }
    74→
    75→  // Fallback: return the last summary if no valid one found
    76→  return summaries.at(-1)?.summary;
    77→}
    78→
    79→interface ExtractSessionOptions {
    80→  rawPath: string;
    81→  outputPath: string;
    82→  agentId?: string;
    83→}
    84→
    85→export async function extractSession(options: ExtractSessionOptions) {
    86→  const { rawPath, outputPath, agentId } = options;
    87→
    88→  const [content, rawStat, machineId] = await Promise.all([
    89→    readFile(rawPath, "utf-8"),
    90→    stat(rawPath),
    91→    getMachineId(),
    92→  ]);
    93→
    94→  const entries: ExtractedEntry[] = [];
    95→
    96→  for (const rawEntry of parseJsonl(content)) {
    97→    const transformed = transformEntry(rawEntry);
    98→    if (transformed) {
    99→      entries.push(transformed);
   100→    }
   101→  }
   102→
   103→  // Skip sessions with no assistant messages
   104→  const hasAssistantMessage = entries.some((e) => e.type === "assistant");
   105→  if (!hasAssistantMessage) {
   106→    return null;
   107→  }
   108→
   109→  // For agent sessions, extract parentSessionId from first entry with sessionId
   110→  const parentSessionId = agentId
   111→    ? entries.find((e): e is ExtractedEntry & { sessionId: string } => "sessionId" in e && typeof e.sessionId === "string")?.sessionId
   112→    : undefined;
   113→
   114→  const summary = findValidSummary(entries);
   115→  const sessionId = basename(rawPath, ".jsonl");
   116→
   117→  const meta: HiveMindMeta = {
   118→    _type: "hive-mind-meta",
   119→    version: HIVE_MIND_VERSION,
   120→    sessionId,
   121→    machineId,
   122→    extractedAt: new Date().toISOString(),
   123→    rawMtime: rawStat.mtime.toISOString(),
   124→    messageCount: entries.length,
   125→    summary,
   126→    rawPath,
   127→    ...(agentId && { agentId }),
   128→    ...(parentSessionId && { parentSessionId }),
   129→  };
   130→
   131→  const sanitizedEntries = entries.map((e) => sanitizeDeep(e));
   132→
   133→  // Write output
   134→  await mkdir(dirname(outputPath), { recursive: true });
   135→
   136→  const lines = [
   137→    JSON.stringify(meta),
   138→    ...sanitizedEntries.map((e) => JSON.stringify(e)),
   139→  ];
   140→
   141→  await writeFile(outputPath, `${lines.join("\n")}\n`);
   142→
   143→  return { messageCount: entries.length, summary };
   144→}
   145→
   146→async function readFirstLine(filePath: string): Promise<string | null> {
   147→  const stream = createReadStream(filePath, { encoding: "utf-8" });
   148→  const rl = createInterface({ input: stream, crlfDelay: Infinity });
   149→
   150→  try {
   151→    for await (const line of rl) {
   152→      stream.destroy();
   153→      return line;
   154→    }
   155→    return null;
   156→  } finally {
   157→    rl.close();
   158→  }
   159→}
   160→
   161→export async function readExtractedMeta(
   162→  extractedPath: string,
   163→): Promise<HiveMindMeta | null> {
   164→  try {
   165→    const firstLine = await readFirstLine(extractedPath);
   166→    if (!firstLine) return null;
   167→
   168→    const parsed = HiveMindMetaSchema.safeParse(JSON.parse(firstLine));
   169→    return parsed.success ? parsed.data : null;
   170→  } catch {
   171→    return null;
   172→  }
   173→}
   174→
   175→export function getProjectsDir(cwd: string): string {
   176→  // Encode cwd: replace / with -
   177→  const encoded = cwd.replace(/\//g, "-");
   178→  return join(homedir(), ".claude", "projects", encoded);
   179→}
   180→
   181→export function getHiveMindSessionsDir(projectCwd: string): string {
   182→  return join(projectCwd, ".claude", "hive-mind", "sessions");
   183→}
   184→
   185→/** Returns both regular sessions and agent sessions. */
   186→export async function findRawSessions(rawDir: string) {
   187→  try {
   188→    const files = await readdir(rawDir);
   189→    const sessions: Array<{ path: string; agentId?: string }> = [];
   190→
   191→    for (const f of files) {
   192→      if (!f.endsWith(".jsonl")) continue;
   193→
   194→      if (f.startsWith("agent-")) {
   195→        // Agent session: agent-<agentId>.jsonl
   196→        const agentId = f.replace("agent-", "").replace(".jsonl", "");
   197→        sessions.push({ path: join(rawDir, f), agentId });
   198→      } else {
   199→        // Regular session
   200→        sessions.push({ path: join(rawDir, f) });
   201→      }
   202→    }
   203→
   204→    return sessions;
   205→  } catch {
   206→    return [];
   207→  }
   208→}
   209→
   210→export async function needsExtraction(
   211→  rawPath: string,
   212→  extractedPath: string,
   213→): Promise<boolean> {
   214→  try {
   215→    const rawStat = await stat(rawPath);
   216→    const meta = await readExtractedMeta(extractedPath);
   217→
   218→    if (!meta) return true;
   219→
   220→    // Compare mtimes
   221→    const rawMtime = rawStat.mtime.toISOString();
   222→    return rawMtime !== meta.rawMtime;
   223→  } catch {
   224→    return true;
   225→  }
   226→}
   227→
   228→export async function extractAllSessions(cwd: string, transcriptPath?: string) {
   229→  // Determine raw sessions directory
   230→  const rawDir = transcriptPath ? dirname(transcriptPath) : getProjectsDir(cwd);
   231→  const extractedDir = getHiveMindSessionsDir(cwd);
   232→
   233→  const rawSessions = await findRawSessions(rawDir);
   234→  let extracted = 0;
   235→
   236→  for (const session of rawSessions) {
   237→    const { path: rawPath, agentId } = session;
   238→
   239→    // Use same filename as original (agent-<id>.jsonl or <sessionId>.jsonl)
   240→    const extractedPath = join(extractedDir, basename(rawPath));
   241→
   242→    if (await needsExtraction(rawPath, extractedPath)) {
   243→      try {
   244→        const result = await extractSession({ rawPath, outputPath: extractedPath, agentId });
   245→        if (result) {
   246→          extracted++;
   247→        }
   248→      } catch (error) {
   249→        // Log but continue with other sessions
   250→        const id = basename(rawPath, ".jsonl");
   251→        console.error(`Failed to extract ${id}:`, error);
   252→      }
   253→    }
   254→  }
   255→
   256→  return extracted;
   257→}
