     1→/**
     2→ * Secret detection and sanitization using patterns ported from gitleaks.
     3→ * Detected secrets are replaced with [REDACTED:<rule-id>].
     4→ *
     5→ * Based on: https://github.com/gitleaks/gitleaks
     6→ * See: https://lookingatcomputer.substack.com/p/regex-is-almost-all-you-need
     7→ */
     8→
     9→import { ALL_KEYWORDS, SECRET_RULES } from "./secret-rules";
    10→
    11→// Maximum recursion depth for sanitizeDeep to prevent stack overflow
    12→const MAX_SANITIZE_DEPTH = 100;
    13→
    14→// Minimum string length to scan for secrets
    15→const MIN_SECRET_LENGTH = 8;
    16→
    17→/**
    18→ * Quick check if a string might contain secrets.
    19→ * Returns false if string contains none of the keywords.
    20→ */
    21→function mightContainSecrets(content: string): boolean {
    22→  const lower = content.toLowerCase();
    23→  for (const keyword of ALL_KEYWORDS) {
    24→    if (lower.includes(keyword)) {
    25→      return true;
    26→    }
    27→  }
    28→  return false;
    29→}
    30→
    31→export interface SecretMatch {
    32→  ruleId: string;
    33→  match: string;
    34→  start: number;
    35→  end: number;
    36→  entropy?: number;
    37→}
    38→
    39→/**
    40→ * Calculate Shannon entropy of a string.
    41→ * Higher entropy = more random = more likely to be a real secret.
    42→ * Typical thresholds: 3.0-4.0 bits per character.
    43→ */
    44→function shannonEntropy(data: string): number {
    45→  if (!data) return 0;
    46→
    47→  const charCounts = new Map<string, number>();
    48→  for (const char of data) {
    49→    charCounts.set(char, (charCounts.get(char) || 0) + 1);
    50→  }
    51→
    52→  let entropy = 0;
    53→  const len = data.length;
    54→  for (const count of charCounts.values()) {
    55→    const freq = count / len;
    56→    entropy -= freq * Math.log2(freq);
    57→  }
    58→
    59→  return entropy;
    60→}
    61→
    62→/**
    63→ * Detect secrets in a string using gitleaks patterns.
    64→ * Returns all matches with their positions and entropy.
    65→ */
    66→export function detectSecrets(content: string): SecretMatch[] {
    67→  if (content.length < MIN_SECRET_LENGTH) {
    68→    return [];
    69→  }
    70→
    71→  const matches: SecretMatch[] = [];
    72→
    73→  // Quick pre-filter: skip strings that don't contain any keywords
    74→  // This is an optimization - rules without keywords will still run
    75→  const lowerContent = content.toLowerCase();
    76→  const hasAnyKeyword = mightContainSecrets(content);
    77→
    78→  for (const rule of SECRET_RULES) {
    79→    // Per-rule keyword filter: skip rules whose keywords don't match
    80→    // Rules without keywords always run (e.g., AWS access tokens)
    81→    if (rule.keywords && rule.keywords.length > 0) {
    82→      // If no keywords matched globally, skip rules with keywords
    83→      if (!hasAnyKeyword) continue;
    84→      const hasKeyword = rule.keywords.some((k) => lowerContent.includes(k));
    85→      if (!hasKeyword) continue;
    86→    }
    87→    // Reset regex state for global matching
    88→    rule.regex.lastIndex = 0;
    89→
    90→    let match: RegExpExecArray | null;
    91→    while ((match = rule.regex.exec(content)) !== null) {
    92→      // Get the captured group (if any) or full match
    93→      const secretValue = match[1] || match[0];
    94→      const start = match.index;
    95→      const end = start + match[0].length;
    96→
    97→      // Calculate entropy if rule has threshold
    98→      const entropy = rule.entropy ? shannonEntropy(secretValue) : undefined;
    99→
   100→      // Skip if entropy is below threshold
   101→      if (rule.entropy && entropy !== undefined && entropy < rule.entropy) {
   102→        continue;
   103→      }
   104→
   105→      matches.push({
   106→        ruleId: rule.id,
   107→        match: match[0],
   108→        start,
   109→        end,
   110→        entropy,
   111→      });
   112→
   113→      // Prevent infinite loops on zero-width matches
   114→      if (match[0].length === 0) {
   115→        rule.regex.lastIndex++;
   116→      }
   117→    }
   118→  }
   119→
   120→  // Sort by position (for consistent replacement order)
   121→  matches.sort((a, b) => a.start - b.start);
   122→
   123→  // Dedupe overlapping matches (keep the first/longest)
   124→  const deduped: SecretMatch[] = [];
   125→  for (const m of matches) {
   126→    const last = deduped[deduped.length - 1];
   127→    if (!last || m.start >= last.end) {
   128→      deduped.push(m);
   129→    }
   130→  }
   131→
   132→  return deduped;
   133→}
   134→
   135→/**
   136→ * Sanitize a string by replacing detected secrets with [REDACTED:<rule-id>].
   137→ */
   138→export function sanitizeString(content: string): string {
   139→  if (content.length < MIN_SECRET_LENGTH) {
   140→    return content;
   141→  }
   142→
   143→  const secrets = detectSecrets(content);
   144→
   145→  if (secrets.length === 0) {
   146→    return content;
   147→  }
   148→
   149→  // Replace from end to start to preserve positions
   150→  let result = content;
   151→  for (let i = secrets.length - 1; i >= 0; i--) {
   152→    const secret = secrets[i];
   153→    result = `${result.slice(0, secret.start)}[REDACTED:${secret.ruleId}]${result.slice(secret.end)}`;
   154→  }
   155→
   156→  return result;
   157→}
   158→
   159→/**
   160→ * Recursively sanitize all strings in an object or array.
   161→ * Returns a new object/array with sanitized strings.
   162→ */
   163→export function sanitizeDeep<T>(value: T, depth = 0): T {
   164→  // Prevent stack overflow from deeply nested or circular structures
   165→  if (depth > MAX_SANITIZE_DEPTH) {
   166→    return value;
   167→  }
   168→
   169→  if (value === null || value === undefined) {
   170→    return value;
   171→  }
   172→
   173→  if (typeof value === "string") {
   174→    return sanitizeString(value) as T;
   175→  }
   176→
   177→  if (Array.isArray(value)) {
   178→    return value.map((item) => sanitizeDeep(item, depth + 1)) as T;
   179→  }
   180→
   181→  if (typeof value === "object") {
   182→    const result: Record<string, unknown> = {};
   183→    for (const [key, val] of Object.entries(value)) {
   184→      result[key] = sanitizeDeep(val, depth + 1);
   185→    }
   186→    return result as T;
   187→  }
   188→
   189→  // Primitives (numbers, booleans) pass through unchanged
   190→  return value;
   191→}
